const loader = new DataLoader();

type UserDataType = Awaited<ReturnType<typeof loader.loadUser>>;
// { id: number; name: string }

type UserWithPostsType = Awaited<ReturnType<typeof loader.loadUserWithPosts>>;
// {
//   user: { id: number; name: string };
//   posts: { id: number; title: string }[]; // –î–∞–∂–µ –≤–ª–æ–∂–µ–Ω–Ω—ã–π Promise —Ä–∞–∑–≤–µ—Ä–Ω—É–ª—Å—è!
// }

// –ü—Ä–∞–∫—Ç–∏—á–µ—Å–∫–∏–π –ø—Ä–∏–º–µ—Ä 3: Thenable –æ–±—ä–µ–∫—Ç—ã (–Ω–µ –æ–±—è–∑–∞—Ç–µ–ª—å–Ω–æ Promise)
class CustomThenable {
