  ? U extends Promise<any>
    ? DeepUnwrap<U> // –†–µ–∫—É—Ä—Å–∏–≤–Ω–∞—è —Ä–∞–∑–≤–µ—Ä—Ç–∫–∞
    : U
  : T;

