```

#### 4. **–†–µ–∞–ª—å–Ω—ã–µ async –ø–∞—Ç—Ç–µ—Ä–Ω—ã**

```typescript
// –ü—Ä–∞–∫—Ç–∏—á–µ—Å–∫–∏–π –ø—Ä–∏–º–µ—Ä 1: Retry –º–µ—Ö–∞–Ω–∏–∑–º
async function fetchWithRetry<T>(
  fn: () => Promise<T>,
  retries: number = 3
): Promise<T> {
  for (let i = 0; i < retries; i++) {
    try {
      return await fn();
    } catch (error) {
      if (i === retries - 1) throw error;
      await new Promise(resolve => setTimeout(resolve, 1000 * Math.pow(2, i)));
    }
  }
  throw new Error("Should not reach here");
}

const data = await fetchWithRetry(async () => {
  const response = await fetch("https://api.example.com/data");
  return response.json();
});

type DataType = Awaited<ReturnType<typeof fetchWithRetry>>;
// unknown (—Ç.–∫. response.json() –≤–æ–∑–≤—Ä–∞—â–∞–µ—Ç unknown)

// –ë–æ–ª–µ–µ —Ç–∏–ø–∏–∑–∏—Ä–æ–≤–∞–Ω–Ω—ã–π –≤–∞—Ä–∏–∞–Ω—Ç
async function fetchWithRetryTyped<T>(
  fn: () => Promise<T>,
  retries: number = 3
): Promise<T> {
  for (let i = 0; i < retries; i++) {
    try {
      return await fn();
    } catch (error) {
      if (i === retries - 1) throw error;
      await new Promise(resolve => setTimeout(resolve, 1000 * Math.pow(2, i)));
    }
  }
  throw new Error("Should not reach here");
}

type UserType = Awaited<ReturnType<typeof fetchWithRetryTyped<{ id: number; name: string }>>>;
// { id: number; name: string }

// –ü—Ä–∞–∫—Ç–∏—á–µ—Å–∫–∏–π –ø—Ä–∏–º–µ—Ä 2: Promise.all —Å —Ç–∏–ø–∏–∑–∞—Ü–∏–µ–π
async function loadData() {
  const [users, posts, comments] = await Promise.all([
    fetch("/api/users").then(r => r.json()),
    fetch("/api/posts").then(r => r.json()),
    fetch("/api/comments").then(r => r.json()),
  ]);

  return { users, posts, comments };
}

type LoadedData = Awaited<ReturnType<typeof loadData>>;
// { users: unknown; posts: unknown; comments: unknown }

// –ë–æ–ª–µ–µ —Ç–∏–ø–∏–∑–∏—Ä–æ–≤–∞–Ω–Ω—ã–π
async function loadDataTyped() {
  interface User { id: number; name: string }
  interface Post { id: number; title: string }
  interface Comment { id: number; text: string }

  const users = await fetch("/api/users").then((r) => r.json() as Promise<User[]>);
  const posts = await fetch("/api/posts").then((r) => r.json() as Promise<Post[]>);
  const comments = await fetch("/api/comments").then((r) => r.json() as Promise<Comment[]>);

