    Promise.resolve(1),
    Promise.resolve(2),
    Promise.resolve(3),
  ];

  for await (const value of streamData(promises)) {
    console.log(value); // 1, 2, 3
  }
})();

// –ü—Ä–∞–∫—Ç–∏—á–µ—Å–∫–∏–π –ø—Ä–∏–º–µ—Ä 3: –¢—Ä–∞–Ω—Å—Ñ–æ—Ä–º–∞—Ü–∏—è Promise —Ü–µ–ø–æ—á–∫–∏
async function transformChain<T, U>(
  promise: Promise<T>,
  transform: (value: T) => Promise<U>
): Promise<U> {
  const value = await promise;
  return transform(value);
}

type TransformResult = Awaited<ReturnType<typeof transformChain<number, string>>>;
// string

// –ü—Ä–∞–∫—Ç–∏—á–µ—Å–∫–∏–π –ø—Ä–∏–º–µ—Ä 4: –û–±—Ä–∞–±–æ—Ç–∫–∞ Finally
async function withFinally<T>(
  fn: () => Promise<T>,
  cleanup: () => Promise<void>
): Promise<T> {
  try {
    return await fn();
  } finally {
    await cleanup();
  }
}

type FinallyResult = Awaited<ReturnType<typeof withFinally<{ data: number }>>>;
