// –ü—Ä–∞–∫—Ç–∏—á–µ—Å–∫–∏–π –ø—Ä–∏–º–µ—Ä 5: Middleware –¥–ª—è –∞—Å–∏–Ω—Ö—Ä–æ–Ω–Ω—ã—Ö –æ–ø–µ—Ä–∞—Ü–∏–π
type AsyncMiddleware<T> = (
  value: T,
  next: (v: T) => Promise<T>
) => Promise<T>;

class AsyncPipeline<T> {
  private middlewares: AsyncMiddleware<T>[] = [];

  use(middleware: AsyncMiddleware<T>): this {
    this.middlewares.push(middleware);
    return this;
  }

  async execute(value: T): Promise<T> {
    let current = value;

    for (const middleware of this.middlewares) {
      current = await middleware(current, async (v) => v);
    }

    return current;
  }
}

// –ò—Å–ø–æ–ª—å–∑–æ–≤–∞–Ω–∏–µ
