    return next(value);
  })
  .use(async (value, next) => {
    value.count += 10;
    return next(value);
  });

(async () => {
  const result = await pipeline.execute({ count: 5 });
  console.log("After:", result.count); // 15

  type ResultType = Awaited<ReturnType<typeof pipeline.execute>>;
  // { count: number }
})();
```

