```typescript
// –ú–µ—Ç–æ–¥—ã Function —Å —è–≤–Ω–æ–π —Ç–∏–ø–∏–∑–∞—Ü–∏–µ–π
interface Function {
  call<T>(this: Function, thisArg: T, ...args: any[]): any;
  apply<T>(this: Function, thisArg: T, args: any[]): any;
