#### –ö–æ–Ω—Ç–µ–∫—Å—Ç extraction –∏ —Ç–∏–ø–∏–∑–∞—Ü–∏—è

```typescript
// –ò–∑–≤–ª–µ—á–µ–Ω–∏–µ –∫–æ–Ω—Ç–µ–∫—Å—Ç–∞ –∏–∑ –º–µ—Ç–æ–¥–∞
type ExtractThisContext<T extends Function> =
  T extends (this: infer This, ...args: any[]) => any ? This : never;

class EventEmitter {
  private listeners: Map<string, Function[]> = new Map();

  on(this: EventEmitter, event: string, callback: Function): void {
    if (!this.listeners.has(event)) {
      this.listeners.set(event, []);
    }
    this.listeners.get(event)!.push(callback);
  }

  emit(this: EventEmitter, event: string, ...args: any[]): void {
    const callbacks = this.listeners.get(event) || [];
    callbacks.forEach(cb => cb(...args));
  }
}

type EmitterContext = ExtractThisContext<EventEmitter['emit']>; // EventEmitter

// –ü—Ä–∞–∫—Ç–∏—á–µ—Å–∫–∏–π –ø—Ä–∏–º–µ—Ä: –∫–æ–Ω—Ç–µ–∫—Å—Ç-–∑–∞–≤–∏—Å–∏–º—ã–µ –æ–±—Ä–∞–±–æ—Ç—á–∏–∫–∏
class DataStore {
  data: Map<string, any> = new Map();

  subscribe(
    this: DataStore,
    key: string,
    callback: (this: DataStore, value: any) => void
  ): void {
    const originalCallback = callback;

    callback.call(this, this.data.get(key));

    // –í–Ω—É—Ç—Ä–µ–Ω–Ω–µ —Å–æ—Ö—Ä–∞–Ω—è–µ–º –ø—Ä–∏–≤—è–∑–∞–Ω–Ω—ã–π callback
    const boundCallback = originalCallback.bind(this);
  }

  update(this: DataStore, key: string, value: any): void {
    this.data.set(key, value);
  }
}

// –ò—Å–ø–æ–ª—å–∑–æ–≤–∞–Ω–∏–µ
