// –ë–∞–∑–æ–≤—ã–π –∏–Ω—Ç–µ—Ä—Ñ–µ–π—Å –¥–ª—è –≤—Å–µ—Ö —Ñ–∏–≥—É—Ä
interface Shape {
  readonly type: string;
  area(): number;
  perimeter(): number;
}

// –ö–æ–Ω–∫—Ä–µ—Ç–Ω—ã–µ —Ñ–∏–≥—É—Ä—ã
interface Circle extends Shape {
  type: 'circle';
  radius: number;
}

interface Rectangle extends Shape {
  type: 'rectangle';
  width: number;
  height: number;
}

interface Triangle extends Shape {
  type: 'triangle';
  base: number;
  height: number;
  sideA: number;
  sideB: number;
  sideC: number;
}

// –û–ø—Ü–∏–∏ –¥–ª—è —Å–æ–∑–¥–∞–Ω–∏—è —Ñ–∏–≥—É—Ä (mapped type)
type ShapeOptions<T extends Shape> = T extends Circle
  ? { radius: number }
  : T extends Rectangle
  ? { width: number; height: number }
  : T extends Triangle
  ? { base: number; height: number; sideA: number; sideB: number; sideC: number }
  : never;

// Generic —Ñ–∞–±—Ä–∏–∫–∞
interface ShapeFactory<T extends Shape> {
  create(options: ShapeOptions<T>): T;
}

class CircleFactory implements ShapeFactory<Circle> {
  create(options: ShapeOptions<Circle>): Circle {
    return {
      type: 'circle',
      radius: options.radius,
      area: () => Math.PI * options.radius ** 2,
      perimeter: () => 2 * Math.PI * options.radius,
    };
  }
}

class RectangleFactory implements ShapeFactory<Rectangle> {
  create(options: ShapeOptions<Rectangle>): Rectangle {
    return {
      type: 'rectangle',
      width: options.width,
      height: options.height,
      area: () => options.width * options.height,
      perimeter: () => 2 * (options.width + options.height),
    };
  }
}

// Abstract Factory —Å generic —Ç–∏–ø–æ–º
type ShapeType = 'circle' | 'rectangle' | 'triangle';

class ShapeFactoryRegistry {
  private factories = new Map<ShapeType, ShapeFactory<any>>();

  register<T extends Shape>(type: ShapeType, factory: ShapeFactory<T>): void {
    this.factories.set(type, factory);
  }

  create<T extends Shape>(type: ShapeType, options: ShapeOptions<T>): T {
    const factory = this.factories.get(type);
    if (!factory) {
      throw new Error(`Factory for type ${type} not found`);
    }
    return factory.create(options);
  }
}

// –ò—Å–ø–æ–ª—å–∑–æ–≤–∞–Ω–∏–µ
const registry = new ShapeFactoryRegistry();
registry.register('circle', new CircleFactory());
registry.register('rectangle', new RectangleFactory());

const circle = registry.create('circle', { radius: 5 });
const rect = registry.create('rectangle', { width: 10, height: 20 });

