// –ò–Ω—Ç–µ—Ä—Ñ–µ–π—Å —Å—Ç—Ä–∞—Ç–µ–≥–∏–∏ —Å generic —Ç–∏–ø–æ–º
interface SortStrategy<T> {
  sort(items: T[], compareFn?: (a: T, b: T) => number): T[];
}

class QuickSort<T> implements SortStrategy<T> {
  sort(items: T[], compareFn?: (a: T, b: T) => number): T[] {
    if (items.length <= 1) return items;

    const pivot = items[0];
    const left: T[] = [];
    const right: T[] = [];

    const compare = compareFn || ((a: any, b: any) => a - b);

    for (let i = 1; i < items.length; i++) {
      if (compare(items[i], pivot) < 0) {
        left.push(items[i]);
      } else {
        right.push(items[i]);
      }
    }

    return [...this.sort(left, compareFn), pivot, ...this.sort(right, compareFn)];
  }
}

class MergeSort<T> implements SortStrategy<T> {
  sort(items: T[], compareFn?: (a: T, b: T) => number): T[] {
    if (items.length <= 1) return items;

    const middle = Math.floor(items.length / 2);
    const left = items.slice(0, middle);
    const right = items.slice(middle);

    return this.merge(
      this.sort(left, compareFn),
      this.sort(right, compareFn),
      compareFn
    );
  }

  private merge(left: T[], right: T[], compareFn?: (a: T, b: T) => number): T[] {
    const result: T[] = [];
    const compare = compareFn || ((a: any, b: any) => a - b);

    let i = 0, j = 0;

    while (i < left.length && j < right.length) {
      if (compare(left[i], right[j]) <= 0) {
        result.push(left[i++]);
      } else {
        result.push(right[j++]);
      }
    }

    return result.concat(left.slice(i)).concat(right.slice(j));
  }
}

// –ö–æ–Ω—Ç–µ–∫—Å—Ç —Å—Ç—Ä–∞—Ç–µ–≥–∏–∏
class Sorter<T> {
  constructor(private strategy: SortStrategy<T>) {}

  setStrategy(strategy: SortStrategy<T>): void {
    this.strategy = strategy;
  }

  sort(items: T[], compareFn?: (a: T, b: T) => number): T[] {
    return this.strategy.sort(items, compareFn);
  }
}

// –ò—Å–ø–æ–ª—å–∑–æ–≤–∞–Ω–∏–µ
const numbers = [5, 2, 8, 1, 9, 3];
const sorter = new Sorter(new QuickSort<number>());
console.log(sorter.sort(numbers)); // [1, 2, 3, 5, 8, 9]

sorter.setStrategy(new MergeSort<number>());
console.log(sorter.sort(numbers)); // [1, 2, 3, 5, 8, 9]

interface Product {
