// 4. OBSERVER PATTERN - Typed Events
// ============================================

// –¢–∏–ø–æ–±–µ–∑–æ–ø–∞—Å–Ω—ã–µ —Å–æ–±—ã—Ç–∏—è
type EventMap = {
  userLoggedIn: { userId: string; timestamp: Date };
  userLoggedOut: { userId: string; reason: string };
  dataUpdated: { entityId: string; changes: Record<string, any> };
  error: { code: string; message: string };
};

type EventHandler<T> = (data: T) => void;

class EventEmitter<Events extends Record<string, any>> {
  private handlers: {
    [K in keyof Events]?: Set<EventHandler<Events[K]>>;
  } = {};

  on<K extends keyof Events>(event: K, handler: EventHandler<Events[K]>): () => void {
    if (!this.handlers[event]) {
      this.handlers[event] = new Set();
    }
    this.handlers[event]!.add(handler);

    // –í–æ–∑–≤—Ä–∞—â–∞–µ–º —Ñ—É–Ω–∫—Ü–∏—é –¥–ª—è –æ—Ç–ø–∏—Å–∫–∏
    return () => this.off(event, handler);
  }

  off<K extends keyof Events>(event: K, handler: EventHandler<Events[K]>): void {
    this.handlers[event]?.delete(handler);
  }

  emit<K extends keyof Events>(event: K, data: Events[K]): void {
    this.handlers[event]?.forEach(handler => handler(data));
  }

  once<K extends keyof Events>(event: K, handler: EventHandler<Events[K]>): void {
    const wrappedHandler: EventHandler<Events[K]> = (data) => {
      handler(data);
      this.off(event, wrappedHandler);
    };
    this.on(event, wrappedHandler);
  }
}

// –ò—Å–ø–æ–ª—å–∑–æ–≤–∞–Ω–∏–µ
const emitter = new EventEmitter<EventMap>();

// ‚úÖ –¢–∏–ø–æ–±–µ–∑–æ–ø–∞—Å–Ω–∞—è –ø–æ–¥–ø–∏—Å–∫–∞
const unsubscribe = emitter.on('userLoggedIn', (data) => {
  console.log(`User ${data.userId} logged in at ${data.timestamp}`);
  // data –∏–º–µ–µ—Ç –ø—Ä–∞–≤–∏–ª—å–Ω—ã–π —Ç–∏–ø: { userId: string; timestamp: Date }
});

emitter.on('error', (data) => {
  console.error(`Error ${data.code}: ${data.message}`);
  // data –∏–º–µ–µ—Ç —Ç–∏–ø: { code: string; message: string }
});

// ‚úÖ –¢–∏–ø–æ–±–µ–∑–æ–ø–∞—Å–Ω–∞—è —ç–º–∏—Å—Å–∏—è
emitter.emit('userLoggedIn', { userId: '123', timestamp: new Date() });

// ‚ùå –û—à–∏–±–∫–∞ –∫–æ–º–ø–∏–ª—è—Ü–∏–∏ - –Ω–µ–ø—Ä–∞–≤–∏–ª—å–Ω—ã–π —Ç–∏–ø –¥–∞–Ω–Ω—ã—Ö
