// 5. SINGLETON PATTERN - Type-Safe Singleton
// ============================================

class DatabaseConnection {
  private static instance: DatabaseConnection | null = null;
  private connectionString: string;
  private isConnected: boolean = false;

  private constructor(connectionString: string) {
    this.connectionString = connectionString;
  }

  static getInstance(connectionString?: string): DatabaseConnection {
    if (!DatabaseConnection.instance) {
      if (!connectionString) {
        throw new Error('Connection string required for first initialization');
      }
      DatabaseConnection.instance = new DatabaseConnection(connectionString);
    }
    return DatabaseConnection.instance;
  }

  connect(): void {
    if (!this.isConnected) {
      console.log(`Connecting to ${this.connectionString}...`);
      this.isConnected = true;
    }
  }

  disconnect(): void {
    if (this.isConnected) {
      console.log('Disconnecting...');
      this.isConnected = false;
    }
  }

  query<T>(sql: string): Promise<T[]> {
    if (!this.isConnected) {
      throw new Error('Not connected to database');
    }
    // –ò–º–∏—Ç–∞—Ü–∏—è –∑–∞–ø—Ä–æ—Å–∞
    return Promise.resolve([] as T[]);
  }

  // –î–ª—è —Ç–µ—Å—Ç–æ–≤ - —Å–±—Ä–æ—Å singleton
  static resetInstance(): void {
    DatabaseConnection.instance = null;
  }
}

// –ò—Å–ø–æ–ª—å–∑–æ–≤–∞–Ω–∏–µ
const db1 = DatabaseConnection.getInstance('postgres://localhost:5432/mydb');
const db2 = DatabaseConnection.getInstance(); // –¢–æ—Ç –∂–µ —ç–∫–∑–µ–º–ø–ª—è—Ä

console.log(db1 === db2); // true

