// DI Container (–ø—Ä–æ—Å—Ç–∞—è —Ä–µ–∞–ª–∏–∑–∞—Ü–∏—è)
type Constructor<T> = new (...args: any[]) => T;

class DIContainer {
  private services = new Map<string, any>();
  private singletons = new Map<string, any>();

  register<T>(token: string, implementation: Constructor<T> | T): void {
    this.services.set(token, implementation);
  }

  registerSingleton<T>(token: string, implementation: Constructor<T> | T): void {
    this.services.set(token, implementation);
    this.singletons.set(token, null);
  }

  resolve<T>(token: string): T {
    const service = this.services.get(token);
    if (!service) {
      throw new Error(`Service ${token} not found`);
    }

    // –ï—Å–ª–∏ —ç—Ç–æ singleton –∏ —É–∂–µ —Å–æ–∑–¥–∞–Ω
    if (this.singletons.has(token)) {
      let instance = this.singletons.get(token);
      if (!instance) {
        instance = typeof service === 'function' ? new service() : service;
        this.singletons.set(token, instance);
      }
      return instance;
    }

    // –°–æ–∑–¥–∞–µ–º –Ω–æ–≤—ã–π —ç–∫–∑–µ–º–ø–ª—è—Ä
    return typeof service === 'function' ? new service() : service;
  }
}

// –ò—Å–ø–æ–ª—å–∑–æ–≤–∞–Ω–∏–µ DI Container
const container = new DIContainer();
container.registerSingleton('ILogger', ConsoleLogger);
container.register('IUserRepository', UserRepository);
container.register('IEmailService', EmailService);

const logger = container.resolve<ILogger>('ILogger');
const userRepo = container.resolve<IUserRepository>('IUserRepository');
const emailService = container.resolve<IEmailService>('IEmailService');

const userService = new UserService(logger, userRepo, emailService);
