function validateUser(data: unknown): User {
  if (!data || typeof data !== 'object') {
    throw new ValidationError('user', 'User data must be an object');
  }

  const user = data as Partial<User>;

  if (!user.name || user.name.trim().length === 0) {
    throw new ValidationError('name', 'Name is required', {
      required: 'Name cannot be empty',
    });
  }

  if (!user.email || !user.email.includes('@')) {
    throw new ValidationError('email', 'Invalid email format', {
      format: 'Email must contain @',
    });
  }

  if (!user.age || user.age < 18) {
    throw new ValidationError('age', 'Invalid age', {
      min: 'Age must be at least 18',
    });
  }

  return user as User;
}

// ============================================
// 2. RESULT PATTERN - Functional Error Handling
// ============================================

// Result type —Å –º–µ—Ç–æ–¥–∞–º–∏ –¥–ª—è —Ä–∞–±–æ—Ç—ã
type Result<T, E = Error> =
  | { success: true; value: T }
  | { success: false; error: E };

// Helper —Ñ—É–Ω–∫—Ü–∏–∏ –¥–ª—è —Å–æ–∑–¥–∞–Ω–∏—è Result
const Ok = <T>(value: T): Result<T, never> => ({ success: true, value });
const Err = <E>(error: E): Result<never, E> => ({ success: false, error });

// –†–∞—Å—à–∏—Ä–µ–Ω–Ω—ã–π Result —Å —É—Ç–∏–ª–∏—Ç–∞–º–∏
class ResultUtil {
  static map<T, U, E>(
    result: Result<T, E>,
    fn: (value: T) => U
  ): Result<U, E> {
    return result.success ? Ok(fn(result.value)) : result;
  }

  static flatMap<T, U, E>(
    result: Result<T, E>,
    fn: (value: T) => Result<U, E>
  ): Result<U, E> {
    return result.success ? fn(result.value) : result;
  }

  static mapError<T, E, F>(
    result: Result<T, E>,
    fn: (error: E) => F
  ): Result<T, F> {
    return result.success ? result : Err(fn(result.error));
  }

  static unwrap<T, E>(result: Result<T, E>): T {
    if (result.success) {
      return result.value;
    }
    throw result.error;
  }

  static unwrapOr<T, E>(result: Result<T, E>, defaultValue: T): T {
    return result.success ? result.value : defaultValue;
  }

  static isOk<T, E>(result: Result<T, E>): result is { success: true; value: T } {
    return result.success;
  }

  static isErr<T, E>(result: Result<T, E>): result is { success: false; error: E } {
    return !result.success;
  }

  // –û–±—ä–µ–¥–∏–Ω–µ–Ω–∏–µ –Ω–µ—Å–∫–æ–ª—å–∫–∏—Ö Result
  static combine<T extends readonly Result<any, any>[]>(
    results: T
  ): Result<
    { [K in keyof T]: T[K] extends Result<infer V, any> ? V : never },
    T[number] extends Result<any, infer E> ? E : never
  > {
    const values: any[] = [];
    for (const result of results) {
      if (!result.success) {
        return result as any;
      }
      values.push(result.value);
    }
    return Ok(values as any);
  }
}

// –ü—Ä–∏–º–µ—Ä—ã –∏—Å–ø–æ–ª—å–∑–æ–≤–∞–Ω–∏—è Result
function divide(a: number, b: number): Result<number, string> {
  if (b === 0) {
    return Err('Division by zero');
  }
  return Ok(a / b);
}

function parseNumber(str: string): Result<number, string> {
  const num = Number(str);
  if (isNaN(num)) {
    return Err(`Invalid number: ${str}`);
  }
  return Ok(num);
}

function sqrt(n: number): Result<number, string> {
  if (n < 0) {
    return Err('Cannot calculate square root of negative number');
  }
  return Ok(Math.sqrt(n));
}

// –¶–µ–ø–æ—á–∫–∞ –æ–ø–µ—Ä–∞—Ü–∏–π —Å Result
function calculateExpression(numStr: string, divisor: number): Result<number, string> {
  return ResultUtil.flatMap(
    parseNumber(numStr),
    (num) => ResultUtil.flatMap(
      divide(num, divisor),
      (result) => sqrt(result)
    )
  );
}

// –ò—Å–ø–æ–ª—å–∑–æ–≤–∞–Ω–∏–µ
const result1 = calculateExpression('16', 4);
if (result1.success) {
  console.log(`Result: ${result1.value}`); // 2
}

const result2 = calculateExpression('invalid', 2);
if (!result2.success) {
  console.error(`Error: ${result2.error}`); // "Invalid number: invalid"
}

// –ö–æ–º–±–∏–Ω–∏—Ä–æ–≤–∞–Ω–∏–µ —Ä–µ–∑—É–ª—å—Ç–∞—Ç–æ–≤
const combined = ResultUtil.combine([
  parseNumber('10'),
  parseNumber('20'),
  parseNumber('30'),
]);

if (combined.success) {
  const [a, b, c] = combined.value;
  console.log(a + b + c); // 60
}

// ============================================
// 3. EITHER MONAD - Functional Composition
// ============================================

type Either<L, R> =
  | { _tag: 'Left'; left: L }
  | { _tag: 'Right'; right: R };

const Left = <L>(value: L): Either<L, never> => ({ _tag: 'Left', left: value });
const Right = <R>(value: R): Either<never, R> => ({ _tag: 'Right', right: value });

class EitherUtil {
  static map<L, R, R2>(
    either: Either<L, R>,
    fn: (value: R) => R2
  ): Either<L, R2> {
    return either._tag === 'Right' ? Right(fn(either.right)) : either;
  }

  static flatMap<L, R, R2>(
    either: Either<L, R>,
    fn: (value: R) => Either<L, R2>
  ): Either<L, R2> {
    return either._tag === 'Right' ? fn(either.right) : either;
  }

  static mapLeft<L, L2, R>(
    either: Either<L, R>,
    fn: (value: L) => L2
  ): Either<L2, R> {
    return either._tag === 'Left' ? Left(fn(either.left)) : either;
  }

  static fold<L, R, T>(
    either: Either<L, R>,
    onLeft: (left: L) => T,
    onRight: (right: R) => T
  ): T {
    return either._tag === 'Left' ? onLeft(either.left) : onRight(either.right);
  }

  static isLeft<L, R>(either: Either<L, R>): either is { _tag: 'Left'; left: L } {
    return either._tag === 'Left';
  }

  static isRight<L, R>(either: Either<L, R>): either is { _tag: 'Right'; right: R } {
    return either._tag === 'Right';
  }
}

// –ò—Å–ø–æ–ª—å–∑–æ–≤–∞–Ω–∏–µ Either
function divideEither(a: number, b: number): Either<string, number> {
  return b === 0 ? Left('Division by zero') : Right(a / b);
}

const eitherResult = EitherUtil.flatMap(
  divideEither(10, 2),
  (result) => divideEither(result, 0)
);

const message = EitherUtil.fold(
  eitherResult,
  (error) => `Error: ${error}`,
  (value) => `Success: ${value}`
);

console.log(message); // "Error: Division by zero"

// ============================================
// 4. TYPE GUARDS –¥–ª—è –æ–±—Ä–∞–±–æ—Ç–∫–∏ –æ—à–∏–±–æ–∫
// ============================================

function handleError(error: unknown): string {
  // Type guard –¥–ª—è ValidationError
  if (error instanceof ValidationError) {
    return `Validation failed for field '${error.field}': ${error.message}`;
  }

  // Type guard –¥–ª—è NotFoundError
  if (error instanceof NotFoundError) {
    return `${error.resource} with id '${error.id}' not found`;
  }

  // Type guard –¥–ª—è AuthenticationError
  if (error instanceof AuthenticationError) {
    return `Authentication failed: ${error.message}`;
  }

  // Type guard –¥–ª—è AuthorizationError
  if (error instanceof AuthorizationError) {
    return `User ${error.userId} cannot ${error.action} ${error.resource}`;
  }

  // Type guard –¥–ª—è NetworkError
  if (error instanceof NetworkError) {
    return `Network error (${error.statusCode || 'unknown'}): ${error.message}`;
  }

  // Type guard –¥–ª—è –ª—é–±–æ–≥–æ AppError
  if (isAppError(error)) {
    return `Application error (${error.code}): ${error.message}`;
  }

  // –°—Ç–∞–Ω–¥–∞—Ä—Ç–Ω—ã–π Error
  if (error instanceof Error) {
    return `Error: ${error.message}`;
  }

  // –ù–µ–∏–∑–≤–µ—Å—Ç–Ω—ã–π —Ç–∏–ø –æ—à–∏–±–∫–∏
  return `Unknown error: ${String(error)}`;
}

// –ë–æ–ª–µ–µ —Å–ª–æ–∂–Ω—ã–π type guard —Å –ø—Ä–æ–≤–µ—Ä–∫–æ–π —Å—Ç—Ä—É–∫—Ç—É—Ä—ã
interface ErrorWithCode {
  code: string;
  message: string;
}

function isErrorWithCode(error: unknown): error is ErrorWithCode {
  return (
    typeof error === 'object' &&
    error !== null &&
    'code' in error &&
    'message' in error &&
    typeof (error as any).code === 'string' &&
    typeof (error as any).message === 'string'
  );
}

// ============================================
// 5. DISCRIMINATED UNIONS –¥–ª—è –æ—à–∏–±–æ–∫
// ============================================

// –û–ø—Ä–µ–¥–µ–ª–µ–Ω–∏–µ –≤—Å–µ—Ö –≤–æ–∑–º–æ–∂–Ω—ã—Ö –æ—à–∏–±–æ–∫
type ApiError =
  | { type: 'validation'; field: string; message: string }
  | { type: 'notFound'; resource: string; id: string }
  | { type: 'unauthorized'; userId?: string }
  | { type: 'forbidden'; userId: string; action: string; resource: string }
  | { type: 'network'; statusCode: number; url: string }
  | { type: 'internal'; message: string; stack?: string };

// Exhaustive checking —Å never type
function formatApiError(error: ApiError): string {
  switch (error.type) {
    case 'validation':
      return `Validation error in ${error.field}: ${error.message}`;
    case 'notFound':
      return `${error.resource} ${error.id} not found`;
    case 'unauthorized':
      return `Unauthorized${error.userId ? ` (user: ${error.userId})` : ''}`;
    case 'forbidden':
      return `User ${error.userId} cannot ${error.action} ${error.resource}`;
    case 'network':
      return `Network error ${error.statusCode} at ${error.url}`;
    case 'internal':
      return `Internal error: ${error.message}`;
    default:
      // –ï—Å–ª–∏ –∑–∞–±—ã–ª–∏ –æ–±—Ä–∞–±–æ—Ç–∞—Ç—å –∫–∞–∫–æ–π-—Ç–æ case, –ø–æ–ª—É—á–∏–º –æ—à–∏–±–∫—É –∫–æ–º–ø–∏–ª—è—Ü–∏–∏
      const exhaustive: never = error;
      throw new Error(`Unhandled error type: ${exhaustive}`);
  }
}

// –§—É–Ω–∫—Ü–∏—è –¥–ª—è —Å–æ–∑–¥–∞–Ω–∏—è —Ç–∏–ø–∏–∑–∏—Ä–æ–≤–∞–Ω–Ω—ã—Ö –æ—à–∏–±–æ–∫
function createApiError(error: ApiError): ApiError {
  return error;
}

// –ò—Å–ø–æ–ª—å–∑–æ–≤–∞–Ω–∏–µ
const errors: ApiError[] = [
  createApiError({ type: 'validation', field: 'email', message: 'Invalid format' }),
  createApiError({ type: 'notFound', resource: 'User', id: '123' }),
  createApiError({ type: 'unauthorized', userId: '456' }),
];

errors.forEach(error => {
  console.log(formatApiError(error));
});

// ============================================
// 6. ASYNC ERROR HANDLING
// ============================================

// Type-safe Promise wrapper
type AsyncResult<T, E = Error> = Promise<Result<T, E>>;

async function fetchUserSafe(id: string): AsyncResult<User, ApiError> {
  try {
    const response = await fetch(`/api/users/${id}`);

    if (!response.ok) {
      if (response.status === 404) {
        return Err({ type: 'notFound', resource: 'User', id });
      }
      if (response.status === 401) {
        return Err({ type: 'unauthorized' });
      }
      return Err({
        type: 'network',
        statusCode: response.status,
        url: response.url,
      });
    }

    const data = await response.json();
    return Ok(data as User);
  } catch (error) {
    return Err({
      type: 'internal',
      message: error instanceof Error ? error.message : 'Unknown error',
      stack: error instanceof Error ? error.stack : undefined,
    });
  }
}

// –ò—Å–ø–æ–ª—å–∑–æ–≤–∞–Ω–∏–µ async Result
async function processUser(id: string): Promise<void> {
  const result = await fetchUserSafe(id);

  if (result.success) {
    console.log(`User: ${result.value.name}`);
  } else {
    console.error(formatApiError(result.error));
  }
}

// –ö–æ–º–±–∏–Ω–∏—Ä–æ–≤–∞–Ω–∏–µ async –æ–ø–µ—Ä–∞—Ü–∏–π
async function fetchMultipleUsers(ids: string[]): AsyncResult<User[], ApiError> {
  const promises = ids.map(id => fetchUserSafe(id));
  const results = await Promise.all(promises);

  // –ü—Ä–æ–≤–µ—Ä—è–µ–º, –≤—Å–µ –ª–∏ —É—Å–ø–µ—à–Ω—ã
  const values: User[] = [];
  for (const result of results) {
    if (!result.success) {
      return result; // –í–æ–∑–≤—Ä–∞—â–∞–µ–º –ø–µ—Ä–≤—É—é –æ—à–∏–±–∫—É
    }
    values.push(result.value);
  }

  return Ok(values);
}

// Try-catch wrapper —Å —Ç–∏–ø–∏–∑–∞—Ü–∏–µ–π
async function tryCatchAsync<T>(
  fn: () => Promise<T>,
  errorHandler?: (error: unknown) => Error
): AsyncResult<T, Error> {
  try {
    const value = await fn();
    return Ok(value);
  } catch (error) {
    const finalError = errorHandler ? errorHandler(error) :
      (error instanceof Error ? error : new Error(String(error)));
    return Err(finalError);
  }
}

// –ò—Å–ø–æ–ª—å–∑–æ–≤–∞–Ω–∏–µ
const userResult = await tryCatchAsync(
  () => fetch('/api/user').then(r => r.json()),
  (error) => new NetworkError('Failed to fetch user', undefined, '/api/user')
);

// ============================================
// 7. ERROR BOUNDARY Pattern (–¥–ª—è React/Angular)
// ============================================

interface ErrorBoundaryState {
  hasError: boolean;
  error?: Error;
  errorInfo?: React.ErrorInfo;
}

// React Error Boundary —Å —Ç–∏–ø–∏–∑–∞—Ü–∏–µ–π
class ErrorBoundary extends React.Component<
  { fallback: (error: Error) => React.ReactNode },
  ErrorBoundaryState
> {
  state: ErrorBoundaryState = { hasError: false };

  static getDerivedStateFromError(error: Error): Partial<ErrorBoundaryState> {
    return { hasError: true, error };
  }

  componentDidCatch(error: Error, errorInfo: React.ErrorInfo): void {
    console.error('Error caught by boundary:', error, errorInfo);
    this.setState({ errorInfo });
  }

  render() {
    if (this.state.hasError && this.state.error) {
      return this.props.fallback(this.state.error);
    }
    return this.props.children;
  }
}
