### Test Utilities

```typescript
// test-utils.ts
import { expectTypeOf } from 'expect-type';

/**
 * –£—Ç–∏–ª–∏—Ç–∞ –¥–ª—è —Å–æ–∑–¥–∞–Ω–∏—è —á–∞—Å—Ç–∏—á–Ω—ã—Ö mock –æ–±—ä–µ–∫—Ç–æ–≤ —Å —Ç–∏–ø–∏–∑–∞—Ü–∏–µ–π
 */
export function createMock<T>(partial: Partial<T> = {}): jest.Mocked<T> {
  const mock = partial as jest.Mocked<T>;

  // –î–æ–±–∞–≤–ª—è–µ–º jest mock —Ñ—É–Ω–∫—Ü–∏–∏ –¥–ª—è –≤—Å–µ—Ö –º–µ—Ç–æ–¥–æ–≤
  Object.keys(mock).forEach(key => {
    const value = mock[key as keyof T];
    if (typeof value === 'function') {
      (mock[key as keyof T] as any) = jest.fn(value);
    }
  });

  return mock;
}

/**
 * –£—Ç–∏–ª–∏—Ç–∞ –¥–ª—è –ø—Ä–æ–≤–µ—Ä–∫–∏ —Ç–∏–ø–æ–≤ –≤ —Ç–µ—Å—Ç–∞—Ö
 */
export function assertType<T>(value: unknown): asserts value is T {
  // Runtime –ø—Ä–æ–≤–µ—Ä–∫–∞ –º–æ–∂–µ—Ç –±—ã—Ç—å –¥–æ–±–∞–≤–ª–µ–Ω–∞ –ø—Ä–∏ –Ω–µ–æ–±—Ö–æ–¥–∏–º–æ—Å—Ç–∏
}

/**
 * –£—Ç–∏–ª–∏—Ç–∞ –¥–ª—è —Å–æ–∑–¥–∞–Ω–∏—è —Ç–∏–ø–∏–∑–∏—Ä–æ–≤–∞–Ω–Ω—ã—Ö spy —Ñ—É–Ω–∫—Ü–∏–π
 */
export function createSpy<T extends (...args: any[]) => any>(): jest.MockedFunction<T> {
  return jest.fn() as jest.MockedFunction<T>;
}

/**
 * –£—Ç–∏–ª–∏—Ç–∞ –¥–ª—è —Å–æ–∑–¥–∞–Ω–∏—è —Ç–∏–ø–∏–∑–∏—Ä–æ–≤–∞–Ω–Ω—ã—Ö fixtures
 */
export class FixtureBuilder<T> {
  private defaults: Partial<T>;

  constructor(defaults: Partial<T>) {
    this.defaults = defaults;
  }

  build(overrides: Partial<T> = {}): T {
    return { ...this.defaults, ...overrides } as T;
  }

  buildList(count: number, overrides: Partial<T> = {}): T[] {
    return Array.from({ length: count }, (_, i) =>
      this.build({ ...overrides, id: i + 1 } as Partial<T>)
    );
  }
}

/**
 * –£—Ç–∏–ª–∏—Ç–∞ –¥–ª—è —Ç–∏–ø–∏–∑–∏—Ä–æ–≤–∞–Ω–Ω–æ–≥–æ deep partial
 */
export type DeepPartial<T> = {
  [P in keyof T]?: T[P] extends object ? DeepPartial<T[P]> : T[P];
};

export function createDeepMock<T>(partial: DeepPartial<T> = {}): T {
  return partial as T;
}
```

