```typescript
// build-cache.ts - —Å–∫—Ä–∏–ø—Ç –¥–ª—è —É–ø—Ä–∞–≤–ª–µ–Ω–∏—è –∫—ç—à–µ–º —Å–±–æ—Ä–∫–∏

import * as fs from 'fs';
import * as path from 'path';
import * as crypto from 'crypto';

interface BuildCache {
  version: string;
  files: Map<string, string>; // path -> hash
  timestamp: number;
}

class BuildCacheManager {
  private cacheFile = '.build-cache.json';
  private cache: BuildCache | null = null;

  load(): void {
    try {
      const data = fs.readFileSync(this.cacheFile, 'utf-8');
      const parsed = JSON.parse(data);
      this.cache = {
        version: parsed.version,
        files: new Map(Object.entries(parsed.files)),
        timestamp: parsed.timestamp,
      };
    } catch {
      this.cache = {
        version: '1.0.0',
        files: new Map(),
        timestamp: Date.now(),
      };
    }
  }

  save(): void {
    if (!this.cache) return;

    const data = {
      version: this.cache.version,
      files: Object.fromEntries(this.cache.files),
      timestamp: this.cache.timestamp,
    };

    fs.writeFileSync(this.cacheFile, JSON.stringify(data, null, 2));
  }

  getFileHash(filePath: string): string {
    const content = fs.readFileSync(filePath);
    return crypto.createHash('sha256').update(content).digest('hex');
  }

  hasFileChanged(filePath: string): boolean {
    if (!this.cache) return true;

    const currentHash = this.getFileHash(filePath);
    const cachedHash = this.cache.files.get(filePath);

    return currentHash !== cachedHash;
  }

  updateFile(filePath: string): void {
    if (!this.cache) return;

    const hash = this.getFileHash(filePath);
    this.cache.files.set(filePath, hash);
  }

  getChangedFiles(srcDir: string): string[] {
    const allFiles = this.getAllTsFiles(srcDir);
    return allFiles.filter(file => this.hasFileChanged(file));
  }

  private getAllTsFiles(dir: string): string[] {
    const files: string[] = [];

    function traverse(currentDir: string): void {
      const entries = fs.readdirSync(currentDir, { withFileTypes: true });

      for (const entry of entries) {
        const fullPath = path.join(currentDir, entry.name);

        if (entry.isDirectory()) {
          if (entry.name !== 'node_modules' && entry.name !== 'dist') {
            traverse(fullPath);
          }
        } else if (entry.name.endsWith('.ts') || entry.name.endsWith('.tsx')) {
          files.push(fullPath);
        }
      }
    }

    traverse(dir);
    return files;
  }
}

// –ò—Å–ø–æ–ª—å–∑–æ–≤–∞–Ω–∏–µ
const cacheManager = new BuildCacheManager();
cacheManager.load();

const changedFiles = cacheManager.getChangedFiles('./src');
console.log(`Changed files: ${changedFiles.length}`);

if (changedFiles.length === 0) {
  console.log('No changes detected, skipping build');
  process.exit(0);
}

// –ó–∞–ø—É—Å–∫–∞–µ–º –∫–æ–º–ø–∏–ª—è—Ü–∏—é —Ç–æ–ª—å–∫–æ –∏–∑–º–µ–Ω—ë–Ω–Ω—ã—Ö —Ñ–∞–π–ª–æ–≤
// ...

// –ü–æ—Å–ª–µ —É—Å–ø–µ—à–Ω–æ–π –∫–æ–º–ø–∏–ª—è—Ü–∏–∏ –æ–±–Ω–æ–≤–ª—è–µ–º –∫—ç—à
changedFiles.forEach(file => cacheManager.updateFile(file));
cacheManager.save();
```

