```typescript
// watch-compiler.ts - –∫–∞—Å—Ç–æ–º–Ω—ã–π watch compiler —Å –æ–ø—Ç–∏–º–∏–∑–∞—Ü–∏—è–º–∏

import * as ts from 'typescript';
import * as path from 'path';

interface WatchConfig {
  configPath: string;
  onSuccess?: () => void;
  onError?: (diagnostics: readonly ts.Diagnostic[]) => void;
}

class CustomWatchCompiler {
  private config: WatchConfig;
  private watchProgram: ts.WatchOfConfigFile<ts.SemanticDiagnosticsBuilderProgram> | null = null;
  private compileCount = 0;
  private startTime = 0;

  constructor(config: WatchConfig) {
    this.config = config;
  }

  start(): void {
    const configPath = ts.findConfigFile(
      './',
      ts.sys.fileExists,
      this.config.configPath
    );

    if (!configPath) {
      throw new Error('Could not find tsconfig.json');
    }

    // –°–æ–∑–¥–∞—ë–º watch compiler host —Å –æ–ø—Ç–∏–º–∏–∑–∞—Ü–∏—è–º–∏
    const createProgram = ts.createSemanticDiagnosticsBuilderProgram;

    const host = ts.createWatchCompilerHost(
      configPath,
      {},
      ts.sys,
      createProgram,
      this.reportDiagnostic.bind(this),
      this.reportWatchStatus.bind(this)
    );

    // –ü–µ—Ä–µ–æ–ø—Ä–µ–¥–µ–ª—è–µ–º –º–µ—Ç–æ–¥—ã –¥–ª—è –æ–ø—Ç–∏–º–∏–∑–∞—Ü–∏–∏
    const origCreateProgram = host.createProgram;
    host.createProgram = (
      rootNames,
      options,
      host,
      oldProgram,
      configFileParsingDiagnostics,
      projectReferences
    ) => {
      console.log(`\n[${new Date().toLocaleTimeString()}] Creating program...`);
      this.startTime = Date.now();

      return origCreateProgram(
        rootNames,
        options,
        host,
        oldProgram,
        configFileParsingDiagnostics,
        projectReferences
      );
    };

    const origPostProgramCreate = host.afterProgramCreate;
    host.afterProgramCreate = program => {
      const elapsed = Date.now() - this.startTime;
      console.log(`Compilation finished in ${elapsed}ms`);

      this.compileCount++;

      if (origPostProgramCreate) {
        origPostProgramCreate(program);
      }
    };

    // –ó–∞–ø—É—Å–∫–∞–µ–º watch
    this.watchProgram = ts.createWatchProgram(host);
  }

  stop(): void {
    if (this.watchProgram) {
      this.watchProgram.close();
      console.log(`\nWatch stopped. Total compilations: ${this.compileCount}`);
    }
  }

  private reportDiagnostic(diagnostic: ts.Diagnostic): void {
    const message = ts.flattenDiagnosticMessageText(
      diagnostic.messageText,
      '\n'
    );

    if (diagnostic.file && diagnostic.start !== undefined) {
      const { line, character } = diagnostic.file.getLineAndCharacterOfPosition(
        diagnostic.start
      );
      const fileName = path.relative(process.cwd(), diagnostic.file.fileName);
      console.error(
        `${fileName}:${line + 1}:${character + 1} - error TS${diagnostic.code}: ${message}`
      );
    } else {
      console.error(`error TS${diagnostic.code}: ${message}`);
    }
  }

  private reportWatchStatus(
    diagnostic: ts.Diagnostic,
    newLine: string,
    options: ts.CompilerOptions,
    errorCount?: number
  ): void {
    const message = ts.flattenDiagnosticMessageText(
      diagnostic.messageText,
      newLine
    );

    // –ö–∞—Å—Ç–æ–º–Ω—ã–µ —Å–æ–æ–±—â–µ–Ω–∏—è –¥–ª—è —Ä–∞–∑–ª–∏—á–Ω—ã—Ö —Å–æ–±—ã—Ç–∏–π
    switch (diagnostic.code) {
      case 6031: // File change detected
        console.log(`\n[${new Date().toLocaleTimeString()}] ${message}`);
        break;
      case 6032: // Starting compilation
        console.log('Starting compilation...');
        break;
      case 6193: // 0 errors
        console.log('‚úÖ Compilation complete with no errors');
        this.config.onSuccess?.();
        break;
      case 6194: // N errors
        console.log(`‚ùå Compilation complete with ${errorCount} error(s)`);
        break;
      default:
        console.log(message);
    }
  }
}

// –ò—Å–ø–æ–ª—å–∑–æ–≤–∞–Ω–∏–µ
const compiler = new CustomWatchCompiler({
  configPath: 'tsconfig.json',
  onSuccess: () => {
    console.log('Running post-compile tasks...');
    // –ó–∞–ø—É—Å–∫ —Ç–µ—Å—Ç–æ–≤, –ª–∏–Ω—Ç–µ—Ä–∞, etc.
  },
  onError: diagnostics => {
    console.error('Compilation failed');
    // –û—Ç–ø—Ä–∞–≤–∫–∞ —É–≤–µ–¥–æ–º–ª–µ–Ω–∏–π, –ª–æ–≥–∏—Ä–æ–≤–∞–Ω–∏–µ
  },
});

compiler.start();

// Graceful shutdown
process.on('SIGINT', () => {
  compiler.stop();
  process.exit(0);
});
```

