// build-info-analyzer.ts - –∞–Ω–∞–ª–∏–∑ .tsbuildinfo

import * as fs from 'fs';

interface TsBuildInfo {
  program: {
    fileNames: string[];
    fileInfos: Record<string, { version: string; signature: string }>;
    options: Record<string, any>;
    referencedMap?: Record<string, string[]>;
    exportedModulesMap?: Record<string, string[]>;
    semanticDiagnosticsPerFile?: any[];
  };
  version: string;
}

function analyzeBuildInfo(path: string): void {
  const content = fs.readFileSync(path, 'utf-8');
  const buildInfo: TsBuildInfo = JSON.parse(content);

  console.log('=== Build Info Analysis ===\n');
  console.log(`TypeScript Version: ${buildInfo.version}`);
  console.log(`Total Files: ${buildInfo.program.fileNames.length}`);

  const fileInfoCount = Object.keys(buildInfo.program.fileInfos).length;
  console.log(`Cached File Infos: ${fileInfoCount}`);

  if (buildInfo.program.referencedMap) {
    const totalDeps = Object.values(buildInfo.program.referencedMap)
      .reduce((sum, deps) => sum + deps.length, 0);
    console.log(`Total Dependencies: ${totalDeps}`);
  }

  // –†–∞–∑–º–µ—Ä —Ñ–∞–π–ª–∞
  const stats = fs.statSync(path);
  console.log(`Build Info Size: ${(stats.size / 1024).toFixed(2)} KB`);
}

// –ò—Å–ø–æ–ª—å–∑–æ–≤–∞–Ω–∏–µ
