import { Injectable, inject } from '@angular/core';
import { HttpClient, HttpParams, HttpHeaders, HttpErrorResponse } from '@angular/common/http';
import { Observable, throwError, BehaviorSubject } from 'rxjs';
import { map, catchError, tap, retry, shareReplay } from 'rxjs/operators';

// Response —Ç–∏–ø—ã
interface ApiResponse<T> {
  data: T;
  message: string;
  status: 'success' | 'error';
}

interface PaginatedResponse<T> {
  items: T[];
  total: number;
  page: number;
  limit: number;
}

interface UserFilters {
  role?: User['role'];
  search?: string;
  sortBy?: keyof User;
  sortOrder?: 'asc' | 'desc';
}

// –°—Ç–∞—Ä—ã–π —Å–ø–æ—Å–æ–± DI (—á–µ—Ä–µ–∑ constructor)
@Injectable({
  providedIn: 'root' // Singleton —Å–µ—Ä–≤–∏—Å
})
export class UserService {
  private apiUrl = '/api/users';
  private usersCache$ = new BehaviorSubject<User[]>([]);

  constructor(private http: HttpClient) {}

  // GET –∑–∞–ø—Ä–æ—Å —Å —Ç–∏–ø–∏–∑–∞—Ü–∏–µ–π
  getUsers(filters?: UserFilters): Observable<User[]> {
    let params = new HttpParams();

    if (filters) {
      if (filters.role) params = params.set('role', filters.role);
      if (filters.search) params = params.set('search', filters.search);
      if (filters.sortBy) params = params.set('sortBy', filters.sortBy);
      if (filters.sortOrder) params = params.set('sortOrder', filters.sortOrder);
    }

    return this.http.get<ApiResponse<User[]>>(this.apiUrl, { params }).pipe(
      map(response => response.data),
      tap(users => this.usersCache$.next(users)),
      retry(3), // –ü–æ–≤—Ç–æ—Ä–∏—Ç—å 3 —Ä–∞–∑–∞ –ø—Ä–∏ –æ—à–∏–±–∫–µ
      catchError(this.handleError)
    );
  }

  // GET —Å –ø–∞—Ä–∞–º–µ—Ç—Ä–æ–º
  getUserById(id: string): Observable<User> {
    return this.http.get<ApiResponse<User>>(`${this.apiUrl}/${id}`).pipe(
      map(response => response.data),
      catchError(this.handleError)
    );
  }

  // POST —Å —Ç–∏–ø–∏–∑–∞—Ü–∏–µ–π body
  createUser(user: Omit<User, 'id' | 'createdAt'>): Observable<User> {
    return this.http.post<ApiResponse<User>>(this.apiUrl, user).pipe(
      map(response => response.data),
      tap(newUser => {
        const currentUsers = this.usersCache$.value;
        this.usersCache$.next([...currentUsers, newUser]);
      }),
      catchError(this.handleError)
    );
  }

  // PUT –¥–ª—è –æ–±–Ω–æ–≤–ª–µ–Ω–∏—è
  updateUser(id: string, updates: Partial<User>): Observable<User> {
    return this.http.put<ApiResponse<User>>(`${this.apiUrl}/${id}`, updates).pipe(
      map(response => response.data),
      tap(updatedUser => {
        const currentUsers = this.usersCache$.value;
        const index = currentUsers.findIndex(u => u.id === id);
        if (index !== -1) {
          currentUsers[index] = updatedUser;
          this.usersCache$.next([...currentUsers]);
        }
      }),
      catchError(this.handleError)
    );
  }

  // DELETE
  deleteUser(id: string): Observable<void> {
    return this.http.delete<void>(`${this.apiUrl}/${id}`).pipe(
      tap(() => {
        const currentUsers = this.usersCache$.value;
        this.usersCache$.next(currentUsers.filter(u => u.id !== id));
      }),
      catchError(this.handleError)
    );
  }

  // –ü–æ–ª—É—á–µ–Ω–∏–µ –∫—ç—à–∞
  get users$(): Observable<User[]> {
    return this.usersCache$.asObservable();
  }

  private handleError(error: HttpErrorResponse): Observable<never> {
    let errorMessage = '–ü—Ä–æ–∏–∑–æ—à–ª–∞ –æ—à–∏–±–∫–∞';

    if (error.error instanceof ErrorEvent) {
      // –ö–ª–∏–µ–Ω—Ç—Å–∫–∞—è –æ—à–∏–±–∫–∞
      errorMessage = `–û—à–∏–±–∫–∞: ${error.error.message}`;
    } else {
      // –°–µ—Ä–≤–µ—Ä–Ω–∞—è –æ—à–∏–±–∫–∞
      errorMessage = `–ö–æ–¥ –æ—à–∏–±–∫–∏: ${error.status}\n–°–æ–æ–±—â–µ–Ω–∏–µ: ${error.message}`;
    }

    console.error(errorMessage);
    return throwError(() => new Error(errorMessage));
  }
}

