import { Observable, of, from, interval, combineLatest, forkJoin } from 'rxjs';
import {
  map,
  filter,
  switchMap,
  mergeMap,
  concatMap,
  debounceTime,
  distinctUntilChanged,
  startWith,
  scan
} from 'rxjs/operators';

@Injectable({
  providedIn: 'root'
})
export class RxJsExamplesService {
  constructor(private userService: UserService) {}

  // –ü—Ä–æ—Å—Ç–æ–π Observable —Å —Ç–∏–ø–æ–º
  getNumbers(): Observable<number> {
    return of(1, 2, 3, 4, 5);
  }

  // –¢—Ä–∞–Ω—Å—Ñ–æ—Ä–º–∞—Ü–∏—è —Ç–∏–ø–æ–≤ —á–µ—Ä–µ–∑ map
  getUserNames(): Observable<string[]> {
    return this.userService.getUsers().pipe(
      map((users: User[]) => users.map((user: User) => user.name))
    );
  }

  // –§–∏–ª—å—Ç—Ä–∞—Ü–∏—è —Å —Å–æ—Ö—Ä–∞–Ω–µ–Ω–∏–µ–º —Ç–∏–ø–∞
  getAdminUsers(): Observable<User[]> {
    return this.userService.getUsers().pipe(
      map((users: User[]) => users.filter((user: User) => user.role === 'admin'))
    );
  }

  // switchMap –¥–ª—è —Å–º–µ–Ω—ã Observable
  getUserWithDetails(userId: string): Observable<User> {
    return this.userService.getUserById(userId).pipe(
      switchMap((user: User) => {
        // –ó–∞–≥—Ä—É–∂–∞–µ–º –¥–æ–ø–æ–ª–Ω–∏—Ç–µ–ª—å–Ω—ã–µ –¥–∞–Ω–Ω—ã–µ
        return this.loadUserDetails(user);
      })
    );
  }

  private loadUserDetails(user: User): Observable<User> {
    // –ò–º–∏—Ç–∞—Ü–∏—è –∑–∞–≥—Ä—É–∑–∫–∏ –¥–µ—Ç–∞–ª–µ–π
    return of({ ...user, avatar: '/assets/avatar.png' });
  }

  // combineLatest –¥–ª—è –∫–æ–º–±–∏–Ω–∏—Ä–æ–≤–∞–Ω–∏—è –Ω–µ—Å–∫–æ–ª—å–∫–∏—Ö Observable
  getCombinedData(): Observable<[User[], string[]]> {
    return combineLatest([
      this.userService.getUsers(),
      this.getUserNames()
    ]);
  }

  // forkJoin –¥–ª—è –ø–∞—Ä–∞–ª–ª–µ–ª—å–Ω—ã—Ö –∑–∞–ø—Ä–æ—Å–æ–≤
  loadMultipleUsers(ids: string[]): Observable<User[]> {
    const requests: Observable<User>[] = ids.map(id =>
      this.userService.getUserById(id)
    );
    return forkJoin(requests);
  }

  // –ü–æ–∏—Å–∫ —Å debounce
  searchUsers(searchTerm$: Observable<string>): Observable<User[]> {
    return searchTerm$.pipe(
      debounceTime(300), // –ñ–¥–µ–º 300ms –ø–æ—Å–ª–µ –ø–æ—Å–ª–µ–¥–Ω–µ–≥–æ –≤–≤–æ–¥–∞
      distinctUntilChanged(), // –¢–æ–ª—å–∫–æ –µ—Å–ª–∏ –∑–Ω–∞—á–µ–Ω–∏–µ –∏–∑–º–µ–Ω–∏–ª–æ—Å—å
      switchMap((term: string) => {
        if (term.length < 2) {
          return of([]);
        }
        return this.userService.getUsers({ search: term });
      })
    );
  }

  // scan –¥–ª—è –∞–∫–∫—É–º—É–ª—è—Ü–∏–∏
  calculateRunningTotal(): Observable<number> {
    return interval(1000).pipe(
      scan((acc: number, curr: number) => acc + curr, 0)
    );
  }
}

