import { Router, ActivatedRoute, ParamMap, NavigationExtras } from '@angular/router';

interface UserRouteParams {
  id: string;
  tab?: 'profile' | 'settings' | 'posts';
}

interface UserQueryParams {
  search?: string;
  page?: number;
  limit?: number;
}

@Component({
  selector: 'app-user-details',
  template: `
    <div *ngIf="user$ | async as user">
      <h1>{{ user.name }}</h1>
      <nav>
        <a [routerLink]="['/users', user.id, 'profile']">–ü—Ä–æ—Ñ–∏–ª—å</a>
        <a [routerLink]="['/users', user.id, 'settings']">–ù–∞—Å—Ç—Ä–æ–π–∫–∏</a>
        <a [routerLink]="['/users', user.id, 'posts']">–ü–æ—Å—Ç—ã</a>
      </nav>
    </div>
  `
})
export class UserDetailsComponent implements OnInit {
  user$!: Observable<User>;
  userId!: string;

  constructor(
    private route: ActivatedRoute,
    private router: Router,
    private userService: UserService
  ) {}

  ngOnInit(): void {
    // –ü–æ–ª—É—á–µ–Ω–∏–µ –ø–∞—Ä–∞–º–µ—Ç—Ä–æ–≤ –º–∞—Ä—à—Ä—É—Ç–∞
    this.route.paramMap.pipe(
      map((params: ParamMap) => params.get('id')),
      filter((id): id is string => id !== null),
      switchMap((id: string) => {
        this.userId = id;
        return this.userService.getUserById(id);
      })
    ).subscribe((user: User) => {
      console.log('User loaded:', user);
    });

    // –ü–æ–ª—É—á–µ–Ω–∏–µ query –ø–∞—Ä–∞–º–µ—Ç—Ä–æ–≤
    this.route.queryParamMap.subscribe((params: ParamMap) => {
      const search = params.get('search') || '';
      const page = parseInt(params.get('page') || '1');
      const limit = parseInt(params.get('limit') || '10');

      console.log('Query params:', { search, page, limit });
    });
  }

  navigateToUser(userId: string): void {
    this.router.navigate(['/users', userId]);
  }

  navigateWithQuery(userId: string, queryParams: UserQueryParams): void {
    const navigationExtras: NavigationExtras = {
      queryParams,
      queryParamsHandling: 'merge' // 'merge' –∏–ª–∏ 'preserve'
    };

    this.router.navigate(['/users', userId], navigationExtras);
  }
}

