import { createAction, createReducer, createSelector, on, props } from '@ngrx/store';
import { createEffect, Actions, ofType } from '@ngrx/effects';

// State interface
interface UserState {
  users: User[];
  selectedUser: User | null;
  loading: boolean;
  error: string | null;
}

const initialState: UserState = {
  users: [],
  selectedUser: null,
  loading: false,
  error: null
};

// Actions —Å —Ç–∏–ø–∏–∑–∞—Ü–∏–µ–π payload
export const loadUsers = createAction('[User] Load Users');

export const loadUsersSuccess = createAction(
  '[User] Load Users Success',
  props<{ users: User[] }>()
);

export const loadUsersFailure = createAction(
  '[User] Load Users Failure',
  props<{ error: string }>()
);

export const selectUser = createAction(
  '[User] Select User',
  props<{ userId: string }>()
);

export const updateUser = createAction(
  '[User] Update User',
  props<{ user: User }>()
);

// Reducer
export const userReducer = createReducer(
  initialState,
  on(loadUsers, (state): UserState => ({
    ...state,
    loading: true,
    error: null
  })),
  on(loadUsersSuccess, (state, { users }): UserState => ({
    ...state,
    users,
    loading: false,
    error: null
  })),
  on(loadUsersFailure, (state, { error }): UserState => ({
    ...state,
    loading: false,
    error
  })),
  on(selectUser, (state, { userId }): UserState => ({
    ...state,
    selectedUser: state.users.find(u => u.id === userId) || null
  })),
  on(updateUser, (state, { user }): UserState => ({
    ...state,
    users: state.users.map(u => u.id === user.id ? user : u),
    selectedUser: state.selectedUser?.id === user.id ? user : state.selectedUser
  }))
);

// Selectors
export interface AppState {
  users: UserState;
}

export const selectUserState = (state: AppState) => state.users;

export const selectAllUsers = createSelector(
  selectUserState,
  (state: UserState): User[] => state.users
);

export const selectSelectedUser = createSelector(
  selectUserState,
  (state: UserState): User | null => state.selectedUser
);

export const selectUserById = (userId: string) => createSelector(
  selectAllUsers,
  (users: User[]): User | undefined => users.find(u => u.id === userId)
);

export const selectLoading = createSelector(
  selectUserState,
  (state: UserState): boolean => state.loading
);

// Effects
@Injectable()
export class UserEffects {
  loadUsers$ = createEffect(() => this.actions$.pipe(
    ofType(loadUsers),
    switchMap(() => this.userService.getUsers().pipe(
      map((users: User[]) => loadUsersSuccess({ users })),
      catchError((error: Error) => of(loadUsersFailure({ error: error.message })))
    ))
  ));

  constructor(
    private actions$: Actions,
    private userService: UserService
  ) {}
}
```

