interface ButtonWithOnClick extends BaseButtonProps {
  variant: 'primary' | 'secondary';
  onClick: () => void;
  href?: never; // –ó–∞–ø—Ä–µ—â–∞–µ–º href
}

interface ButtonWithHref extends BaseButtonProps {
  variant: 'primary' | 'secondary';
  href: string;
  onClick?: never; // –ó–∞–ø—Ä–µ—â–∞–µ–º onClick
}

type ButtonProps = ButtonWithOnClick | ButtonWithHref;

export const Button: FC<ButtonProps> = (props) => {
  const { children, variant = 'primary', disabled = false } = props;

  if ('href' in props && props.href) {
    return (
      <a href={props.href} className={`button ${variant}`}>
        {children}
      </a>
    );
  }

  return (
    <button
      onClick={props.onClick}
      disabled={disabled}
      className={`button ${variant}`}
    >
      {children}
    </button>
  );
};

