// useMemo —Ç–∏–ø–∏–∑–∞—Ü–∏—è
function ExpensiveCalculation({ data }: { data: number[] }): JSX.Element {
  // TypeScript –≤—ã–≤–æ–¥–∏—Ç —Ç–∏–ø –≤–æ–∑–≤—Ä–∞—â–∞–µ–º–æ–≥–æ –∑–Ω–∞—á–µ–Ω–∏—è
  const sum = useMemo(() => {
    console.log('Calculating sum...');
    return data.reduce((acc, num) => acc + num, 0);
  }, [data]);

  // –Ø–≤–Ω–∞—è —Ç–∏–ø–∏–∑–∞—Ü–∏—è –¥–ª—è —Å–ª–æ–∂–Ω—ã—Ö —Ç–∏–ø–æ–≤
  const processedData = useMemo<Map<number, string>>(() => {
    const map = new Map<number, string>();
    data.forEach(num => {
      map.set(num, num.toString());
    });
    return map;
  }, [data]);

  return (
    <div>
      <p>Sum: {sum}</p>
      <p>Processed items: {processedData.size}</p>
    </div>
  );
}

