// Hook –¥–ª—è LocalStorage —Å —Ç–∏–ø–∏–∑–∞—Ü–∏–µ–π
function useLocalStorage<T>(
  key: string,
  initialValue: T
): [T, (value: T | ((prev: T) => T)) => void] {
  // –ò–Ω–∏—Ü–∏–∞–ª–∏–∑–∞—Ü–∏—è state –∏–∑ localStorage
  const [storedValue, setStoredValue] = useState<T>(() => {
    try {
      const item = window.localStorage.getItem(key);
      return item ? JSON.parse(item) : initialValue;
    } catch (error) {
      console.error(error);
      return initialValue;
    }
  });

  // –û–±–µ—Ä—Ç–∫–∞ –¥–ª—è setValue —Å —Å–æ—Ö—Ä–∞–Ω–µ–Ω–∏–µ–º –≤ localStorage
  const setValue = (value: T | ((prev: T) => T)): void => {
    try {
      // –ü–æ–¥–¥–µ—Ä–∂–∫–∞ —Ñ—É–Ω–∫—Ü–∏–æ–Ω–∞–ª—å–Ω–æ–≥–æ –æ–±–Ω–æ–≤–ª–µ–Ω–∏—è
      const valueToStore = value instanceof Function ? value(storedValue) : value;

      setStoredValue(valueToStore);
      window.localStorage.setItem(key, JSON.stringify(valueToStore));
    } catch (error) {
      console.error(error);
    }
  };

  return [storedValue, setValue];
}

// –ò—Å–ø–æ–ª—å–∑–æ–≤–∞–Ω–∏–µ custom hook
function UserSettings(): JSX.Element {
  const [settings, setSettings] = useLocalStorage<{
