// stores/user.ts
import { defineStore } from 'pinia';
import { ref, computed } from 'vue';

interface User {
  id: string;
  name: string;
  email: string;
  role: 'admin' | 'user';
}

interface UserFilters {
  role?: User['role'];
  search?: string;
}

// Setup Stores (—Ä–µ–∫–æ–º–µ–Ω–¥—É–µ—Ç—Å—è –¥–ª—è TypeScript)
export const useUserStore = defineStore('user', () => {
  // State
  const users = ref<User[]>([]);
  const currentUser = ref<User | null>(null);
  const loading = ref<boolean>(false);
  const error = ref<Error | null>(null);

  // Getters (computed)
  const userCount = computed<number>(() => users.value.length);

  const adminUsers = computed<User[]>(() =>
    users.value.filter(u => u.role === 'admin')
  );

  const getUserById = computed(() => {
    return (id: string): User | undefined => {
      return users.value.find(u => u.id === id);
    };
  });

  // Actions
  async function fetchUsers(filters?: UserFilters): Promise<void> {
    loading.value = true;
    error.value = null;

    try {
      const queryParams = new URLSearchParams();
      if (filters?.role) queryParams.set('role', filters.role);
      if (filters?.search) queryParams.set('search', filters.search);

      const response = await fetch(`/api/users?${queryParams}`);
      const data = await response.json();
      users.value = data;
    } catch (e) {
      error.value = e as Error;
    } finally {
      loading.value = false;
    }
  }

  async function fetchUserById(id: string): Promise<User | null> {
    try {
      const response = await fetch(`/api/users/${id}`);
      const user = await response.json();
      return user;
    } catch (e) {
      console.error(e);
      return null;
    }
  }

  function setCurrentUser(user: User): void {
    currentUser.value = user;
  }

  function addUser(user: User): void {
    users.value.push(user);
  }

  function updateUser(id: string, updates: Partial<User>): void {
    const index = users.value.findIndex(u => u.id === id);
    if (index !== -1) {
      users.value[index] = { ...users.value[index], ...updates };
    }
  }

  function removeUser(id: string): void {
    users.value = users.value.filter(u => u.id !== id);
  }

  function clearUsers(): void {
    users.value = [];
    currentUser.value = null;
  }

  return {
    // State
    users,
    currentUser,
    loading,
    error,
    // Getters
    userCount,
    adminUsers,
    getUserById,
    // Actions
    fetchUsers,
    fetchUserById,
    setCurrentUser,
    addUser,
    updateUser,
    removeUser,
    clearUsers
  };
});

// –ò—Å–ø–æ–ª—å–∑–æ–≤–∞–Ω–∏–µ store
