import express, {
  Request,
  Response,
  NextFunction,
  RequestHandler,
  ErrorRequestHandler
} from 'express';

// –ò–Ω—Ç–µ—Ä—Ñ–µ–π—Å—ã –¥–ª—è –¥–∞–Ω–Ω—ã—Ö
interface User {
  id: string;
  name: string;
  email: string;
  role: 'admin' | 'user';
  createdAt: Date;
}

// Request —Å —Ç–∏–ø–∏–∑–∏—Ä–æ–≤–∞–Ω–Ω—ã–º–∏ –ø–∞—Ä–∞–º–µ—Ç—Ä–∞–º–∏ –º–∞—Ä—à—Ä—É—Ç–∞
interface UserParams {
  userId: string;
}

// Request body —Ç–∏–ø–∏–∑–∞—Ü–∏—è
interface CreateUserBody {
  name: string;
  email: string;
  password: string;
  role?: 'admin' | 'user';
}

interface UpdateUserBody {
  name?: string;
  email?: string;
  role?: 'admin' | 'user';
}

// Query parameters —Ç–∏–ø–∏–∑–∞—Ü–∏—è
interface UserQuery {
  limit?: string;
  offset?: string;
  role?: 'admin' | 'user';
  search?: string;
  sortBy?: keyof User;
  sortOrder?: 'asc' | 'desc';
}

// Response body —Ç–∏–ø–∏–∑–∞—Ü–∏—è
interface ApiResponse<T> {
  success: boolean;
  data?: T;
  error?: string;
  message?: string;
}

interface PaginatedResponse<T> {
  items: T[];
  total: number;
  page: number;
  limit: number;
  hasMore: boolean;
}

// ============================================
// 3. –¢–ò–ü–ò–ó–ò–†–û–í–ê–ù–ù–´–ï ROUTE HANDLERS
// ============================================

// –°–ø–æ—Å–æ–± 1: RequestHandler —Å –¥–∂–µ–Ω–µ—Ä–∏–∫–∞–º–∏
const getUser: RequestHandler<UserParams, ApiResponse<User>> = async (req, res, next) => {
  try {
    const { userId } = req.params;

    // –ò–º–∏—Ç–∞—Ü–∏—è –ø–æ–ª—É—á–µ–Ω–∏—è –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è –∏–∑ –ë–î
    const user = await findUserById(userId);

    if (!user) {
      return res.status(404).json({
        success: false,
        error: 'User not found'
      });
    }

    res.json({
      success: true,
      data: user
    });
  } catch (error) {
    next(error);
  }
};

// –°–ø–æ—Å–æ–± 2: –ü—Ä—è–º–∞—è —Ç–∏–ø–∏–∑–∞—Ü–∏—è
async function createUser(
  req: Request<{}, ApiResponse<User>, CreateUserBody>,
  res: Response<ApiResponse<User>>,
  next: NextFunction
): Promise<void> {
  try {
    const { name, email, password, role = 'user' } = req.body;

    // –í–∞–ª–∏–¥–∞—Ü–∏—è
    if (!name || !email || !password) {
      res.status(400).json({
        success: false,
        error: 'Missing required fields'
      });
      return;
    }

    // –°–æ–∑–¥–∞–Ω–∏–µ –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è
    const user: User = {
      id: generateId(),
      name,
      email,
      role,
      createdAt: new Date()
    };

    await saveUser(user);

    res.status(201).json({
      success: true,
      data: user,
      message: 'User created successfully'
    });
  } catch (error) {
    next(error);
  }
}

// Update —Å Partial body
const updateUser: RequestHandler<
  UserParams,
  ApiResponse<User>,
  UpdateUserBody
> = async (req, res, next) => {
  try {
    const { userId } = req.params;
    const updates = req.body;

    const user = await findUserById(userId);

    if (!user) {
      return res.status(404).json({
        success: false,
        error: 'User not found'
      });
    }

    const updatedUser = { ...user, ...updates };
    await saveUser(updatedUser);

    res.json({
      success: true,
      data: updatedUser
    });
  } catch (error) {
    next(error);
  }
};

// List —Å query parameters
const listUsers: RequestHandler<
  {},
  ApiResponse<PaginatedResponse<User>>,
  {},
  UserQuery
> = async (req, res, next) => {
  try {
    const {
      limit = '10',
      offset = '0',
      role,
      search,
      sortBy = 'createdAt',
      sortOrder = 'desc'
    } = req.query;

    // –ü–∞—Ä—Å–∏–Ω–≥ query –ø–∞—Ä–∞–º–µ—Ç—Ä–æ–≤
    const parsedLimit = parseInt(limit, 10);
    const parsedOffset = parseInt(offset, 10);

    // –í–∞–ª–∏–¥–∞—Ü–∏—è
    if (isNaN(parsedLimit) || isNaN(parsedOffset)) {
      return res.status(400).json({
        success: false,
        error: 'Invalid pagination parameters'
      });
    }

    // –ü–æ–ª—É—á–µ–Ω–∏–µ –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª–µ–π
    const { users, total } = await findUsers({
      limit: parsedLimit,
      offset: parsedOffset,
      role,
      search,
      sortBy,
      sortOrder
    });

    res.json({
      success: true,
      data: {
        items: users,
        total,
        page: Math.floor(parsedOffset / parsedLimit) + 1,
        limit: parsedLimit,
        hasMore: parsedOffset + parsedLimit < total
      }
    });
  } catch (error) {
    next(error);
  }
};

// Delete
const deleteUser: RequestHandler<UserParams, ApiResponse<void>> = async (req, res, next) => {
  try {
    const { userId } = req.params;

    const deleted = await deleteUserById(userId);

    if (!deleted) {
      return res.status(404).json({
        success: false,
        error: 'User not found'
      });
    }

    res.json({
      success: true,
      message: 'User deleted successfully'
    });
  } catch (error) {
    next(error);
  }
};

