        {
          id: '1',
          name: 'John Developer',
          email: 'john@test.com'
        } as User
      ];

      const mockQueryBuilder = {
        where: jest.fn().mockReturnThis(),
        orWhere: jest.fn().mockReturnThis(),
        getMany: jest.fn().mockResolvedValue(users)
      };

      userRepository.createQueryBuilder!.mockReturnValue(
