// src/hooks/useLocalStorage.ts
import { useState, useEffect, useCallback, Dispatch, SetStateAction } from 'react';

export interface UseLocalStorageOptions<T> {
  /**
   * –§—É–Ω–∫—Ü–∏—è –¥–ª—è —Å–µ—Ä–∏–∞–ª–∏–∑–∞—Ü–∏–∏ –∑–Ω–∞—á–µ–Ω–∏—è
   * @default JSON.stringify
   */
  serializer?: (value: T) => string;

  /**
   * –§—É–Ω–∫—Ü–∏—è –¥–ª—è –¥–µ—Å–µ—Ä–∏–∞–ª–∏–∑–∞—Ü–∏–∏ –∑–Ω–∞—á–µ–Ω–∏—è
   * @default JSON.parse
   */
  deserializer?: (value: string) => T;

  /**
   * –°–∏–Ω—Ö—Ä–æ–Ω–∏–∑–∏—Ä–æ–≤–∞—Ç—å –º–µ–∂–¥—É —Ç–∞–±–∞–º–∏
   * @default true
   */
  syncTabs?: boolean;
}

type SetValue<T> = Dispatch<SetStateAction<T>>;

/**
 * –•—É–∫ –¥–ª—è —Ä–∞–±–æ—Ç—ã —Å localStorage —Å –ø–æ–ª–Ω–æ–π —Ç–∏–ø–∏–∑–∞—Ü–∏–µ–π –∏ —Å–∏–Ω—Ö—Ä–æ–Ω–∏–∑–∞—Ü–∏–µ–π –º–µ–∂–¥—É —Ç–∞–±–∞–º–∏.
 *
 * @param key - –ö–ª—é—á –≤ localStorage
 * @param initialValue - –ù–∞—á–∞–ª—å–Ω–æ–µ –∑–Ω–∞—á–µ–Ω–∏–µ
 * @param options - –û–ø—Ü–∏–∏ –¥–ª—è –Ω–∞—Å—Ç—Ä–æ–π–∫–∏ –ø–æ–≤–µ–¥–µ–Ω–∏—è
 *
 * @returns –ö–æ—Ä—Ç–µ–∂ [value, setValue, removeValue]
 *
 * @example
 * ```tsx
 * const [user, setUser, removeUser] = useLocalStorage<User>(
 *   'user',
 *   null,
 *   { syncTabs: true }
 * );
 *
 * // –û–±–Ω–æ–≤–∏—Ç—å –∑–Ω–∞—á–µ–Ω–∏–µ
 * setUser({ id: 1, name: 'John' });
 *
 * // –£–¥–∞–ª–∏—Ç—å –∑–Ω–∞—á–µ–Ω–∏–µ
 * removeUser();
 * ```
 */
export function useLocalStorage<T>(
  key: string,
  initialValue: T,
  options: UseLocalStorageOptions<T> = {}
): [T, SetValue<T>, () => void] {
  const {
    serializer = JSON.stringify,
    deserializer = JSON.parse,
    syncTabs = true
  } = options;

  // –ü–æ–ª—É—á–∞–µ–º –Ω–∞—á–∞–ª—å–Ω–æ–µ –∑–Ω–∞—á–µ–Ω–∏–µ
  const readValue = useCallback((): T => {
    if (typeof window === 'undefined') {
      return initialValue;
    }

    try {
      const item = window.localStorage.getItem(key);
      return item ? deserializer(item) : initialValue;
    } catch (error) {
      console.warn(`Error reading localStorage key "${key}":`, error);
      return initialValue;
    }
  }, [initialValue, key, deserializer]);

  const [storedValue, setStoredValue] = useState<T>(readValue);

  // –û–±–Ω–æ–≤–ª—è–µ–º localStorage –ø—Ä–∏ –∏–∑–º–µ–Ω–µ–Ω–∏–∏ –∑–Ω–∞—á–µ–Ω–∏—è
  const setValue: SetValue<T> = useCallback(
    (value) => {
      if (typeof window === 'undefined') {
        console.warn(`Tried to set localStorage key "${key}" in non-browser environment`);
        return;
      }

      try {
        const newValue = value instanceof Function ? value(storedValue) : value;

        window.localStorage.setItem(key, serializer(newValue));
        setStoredValue(newValue);

        // Dispatch storage event –¥–ª—è —Å–∏–Ω—Ö—Ä–æ–Ω–∏–∑–∞—Ü–∏–∏ –º–µ–∂–¥—É —Ç–∞–±–∞–º–∏
        window.dispatchEvent(new Event('local-storage'));
      } catch (error) {
        console.warn(`Error setting localStorage key "${key}":`, error);
      }
    },
    [key, storedValue, serializer]
  );

  // –£–¥–∞–ª—è–µ–º –∑–Ω–∞—á–µ–Ω–∏–µ –∏–∑ localStorage
  const removeValue = useCallback(() => {
    if (typeof window === 'undefined') {
      console.warn(`Tried to remove localStorage key "${key}" in non-browser environment`);
      return;
    }

    try {
      window.localStorage.removeItem(key);
      setStoredValue(initialValue);
      window.dispatchEvent(new Event('local-storage'));
    } catch (error) {
      console.warn(`Error removing localStorage key "${key}":`, error);
    }
  }, [key, initialValue]);

  // –°–∏–Ω—Ö—Ä–æ–Ω–∏–∑–∞—Ü–∏—è –º–µ–∂–¥—É —Ç–∞–±–∞–º–∏
  useEffect(() => {
    if (!syncTabs) return;

    const handleStorageChange = (e: StorageEvent | Event) => {
      if ((e as StorageEvent).key && (e as StorageEvent).key !== key) {
        return;
      }
      setStoredValue(readValue());
    };

    window.addEventListener('storage', handleStorageChange);
    window.addEventListener('local-storage', handleStorageChange);

    return () => {
      window.removeEventListener('storage', handleStorageChange);
      window.removeEventListener('local-storage', handleStorageChange);
    };
  }, [key, readValue, syncTabs]);

  return [storedValue, setValue, removeValue];
}

