  baseURL: import.meta.env.VITE_API_URL,
  timeout: 10000,
  headers: {
    'X-Client-Version': '1.0.0'
  }
});

export const userApi = {
  login: (credentials: UserCredentials) =>
    apiClient.post<{ token: string; user: User }, UserCredentials>(
      '/auth/login',
      credentials
    ),

  getProfile: () =>
    apiClient.get<User>('/user/profile')
};
