// packages/shared/src/domain/entities.ts
export abstract class Entity<T extends { id: string }> {
  constructor(protected props: T) {}

  get id(): string {
    return this.props.id;
  }

  abstract validate(): boolean;

  equals(entity: Entity<T>): boolean {
    return this.id === entity.id;
  }
}

export class UserEntity extends Entity<User> {
  validate(): boolean {
    return (
      !!this.props.email &&
      !!this.props.name &&
      !!this.props.role
    );
  }

  get email(): string {
    return this.props.email;
  }

  get name(): string {
    return this.props.name;
  }

  updateProfile(updates: Partial<User>): UserEntity {
    return new UserEntity({
      ...this.props,
      ...updates,
      updatedAt: new Date()
    });
  }
}

// packages/shared/src/repositories/base.ts
import { Entity } from '../domain/entities';

export interface Repository<T extends Entity<any>> {
  findById(id: string): Promise<T | null>;
  findAll(): Promise<T[]>;
  save(entity: T): Promise<T>;
  delete(id: string): Promise<boolean>;
}

export abstract class InMemoryRepository<T extends Entity<any>>
  implements Repository<T>
{
  protected items: Map<string, T> = new Map();

  async findById(id: string): Promise<T | null> {
    return this.items.get(id) || null;
  }

  async findAll(): Promise<T[]> {
    return Array.from(this.items.values());
  }

  async save(entity: T): Promise<T> {
    this.items.set(entity.id, entity);
    return entity;
  }

  async delete(id: string): Promise<boolean> {
    return this.items.delete(id);
  }
}

// packages/api-server/src/repositories/userRepository.ts
import { UserEntity } from '@monorepo/shared/domain/entities';
import { InMemoryRepository } from '@monorepo/shared/repositories/base';

export class UserRepository extends InMemoryRepository<UserEntity> {
  async findByEmail(email: string): Promise<UserEntity | null> {
    const users = await this.findAll();
    return users.find(user => user.email === email) || null;
  }

  async findByRole(role: UserRole): Promise<UserEntity[]> {
    const users = await this.findAll();
    return users.filter(user => user.props.role === role);
  }
}

// packages/api-server/src/services/userService.ts
import { UserRepository } from '../repositories/userRepository';
import { UserEntity } from '@monorepo/shared/domain/entities';
import { UserCredentials, User } from '@monorepo/shared/types';

export class UserService {
  constructor(private userRepository: UserRepository) {}

  async authenticate(credentials: UserCredentials): Promise<User | null> {
    const user = await this.userRepository.findByEmail(credentials.email);
    if (!user) return null;

    // –ü—Ä–æ–≤–µ—Ä–∫–∞ –ø–∞—Ä–æ–ª—è
    const isValid = await this.verifyPassword(
      credentials.password,
      user.props.password
    );
    if (!isValid) return null;

    return user.props;
  }

  async createUser(data: UserCredentials): Promise<User> {
    const existingUser = await this.userRepository.findByEmail(data.email);
    if (existingUser) {
      throw new Error('User already exists');
    }

    const user = new UserEntity({
      id: generateId(),
      email: data.email,
      name: data.email.split('@')[0],
      role: 'user',
      password: await this.hashPassword(data.password),
      createdAt: new Date(),
      updatedAt: new Date(),
      isActive: true
    });

    if (!user.validate()) {
      throw new Error('Invalid user data');
    }

    await this.userRepository.save(user);
    return user.props;
  }

  private async hashPassword(password: string): Promise<string> {
    // –•–µ—à–∏—Ä–æ–≤–∞–Ω–∏–µ –ø–∞—Ä–æ–ª—è
    return password; // –£–ø—Ä–æ—â–µ–Ω–Ω–æ
  }

  private async verifyPassword(
    password: string,
    hash: string
  ): Promise<boolean> {
    // –ü—Ä–æ–≤–µ—Ä–∫–∞ –ø–∞—Ä–æ–ª—è
    return password === hash; // –£–ø—Ä–æ—â–µ–Ω–Ω–æ
  }
}
