// packages/shared/src/api/client.ts
import { UserApi } from './contracts';

export class TypedApiClient {
  constructor(private baseURL: string) {}

  private async request<T>(
    path: string,
    options?: RequestInit
  ): Promise<T> {
    const response = await fetch(`${this.baseURL}${path}`, options);
    if (!response.ok) {
      throw new Error(`API Error: ${response.statusText}`);
    }
    return response.json();
  }

  user = {
    login: (data: UserApi.Login.Request): Promise<UserApi.Login.Response> =>
      this.request('/auth/login', {
        method: 'POST',
        body: JSON.stringify(data),
        headers: { 'Content-Type': 'application/json' }
      }),

    register: (
      data: UserApi.Register.Request
    ): Promise<UserApi.Register.Response> =>
      this.request('/auth/register', {
        method: 'POST',
        body: JSON.stringify(data),
        headers: { 'Content-Type': 'application/json' }
      }),

    getProfile: (): Promise<UserApi.GetProfile.Response> =>
      this.request('/user/profile'),

    updateProfile: (
      data: UserApi.UpdateProfile.Request
    ): Promise<UserApi.UpdateProfile.Response> =>
      this.request('/user/profile', {
        method: 'PATCH',
        body: JSON.stringify(data),
        headers: { 'Content-Type': 'application/json' }
      }),

    listUsers: (
      params: UserApi.ListUsers.Request
    ): Promise<UserApi.ListUsers.Response> => {
      const query = new URLSearchParams(
        params as Record<string, string>
      ).toString();
      return this.request(`/users?${query}`);
    }
  };
}

