// packages/shared/src/validation/schemas.ts
import { z } from 'zod';

export const UserSchema = z.object({
  id: z.string().uuid(),
  email: z.string().email(),
  name: z.string().min(2).max(100),
  role: z.enum(['admin', 'user', 'guest']),
  createdAt: z.date(),
  updatedAt: z.date(),
  isActive: z.boolean()
});

export const UserCredentialsSchema = z.object({
  email: z.string().email(),
  password: z.string().min(8).max(100)
});

export const RegisterSchema = UserCredentialsSchema.extend({
  name: z.string().min(2).max(100)
});

export type User = z.infer<typeof UserSchema>;
export type UserCredentials = z.infer<typeof UserCredentialsSchema>;
export type RegisterData = z.infer<typeof RegisterSchema>;

// packages/shared/src/validation/validator.ts
import { z } from 'zod';

export class ValidationError extends Error {
  constructor(
    message: string,
    public errors: z.ZodError
  ) {
    super(message);
    this.name = 'ValidationError';
  }
}

export function validate<T>(
  schema: z.ZodSchema<T>,
  data: unknown
): T {
  const result = schema.safeParse(data);
  if (!result.success) {
    throw new ValidationError('Validation failed', result.error);
  }
  return result.data;
}

// –ò—Å–ø–æ–ª—å–∑–æ–≤–∞–Ω–∏–µ –≤ api-server
import { validate, UserCredentialsSchema } from '@monorepo/shared/validation';
import { Request, Response, NextFunction } from 'express';

export const validateBody =
  <T>(schema: z.ZodSchema<T>) =>
  (req: Request, res: Response, next: NextFunction) => {
    try {
      req.body = validate(schema, req.body);
      next();
    } catch (error) {
      if (error instanceof ValidationError) {
        res.status(400).json({
          status: 400,
          message: 'Validation failed',
          errors: error.errors.errors
        });
      } else {
        next(error);
      }
    }
  };

