// remote-cart/src/Cart.tsx
import React, { useState, useEffect } from 'react';
import {
  Cart as CartType,
  CartItem as CartItemType,
  MicroFrontendEvent,
  EventPayload,
  ProductAddedPayload,
  CartUpdatedPayload
} from '@micro-frontends/shared-types';
import { CartItem } from './CartItem';
import { cartApi } from './api/cartApi';
import { eventBus } from './services/EventBus';
import './Cart.css';

export const Cart: React.FC = () => {
  const [cart, setCart] = useState<CartType | null>(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    loadCart();
    subscribeToEvents();
  }, []);

  const loadCart = async () => {
    try {
      setLoading(true);
      const data = await cartApi.getCart();
      setCart(data);
    } catch (err) {
      console.error('Failed to load cart:', err);
    } finally {
      setLoading(false);
    }
  };

  const subscribeToEvents = () => {
    // –°–ª—É—à–∞–µ–º —Å–æ–±—ã—Ç–∏—è –¥–æ–±–∞–≤–ª–µ–Ω–∏—è —Ç–æ–≤–∞—Ä–∞
    eventBus.on<ProductAddedPayload>(
      MicroFrontendEvent.ProductAdded,
      async (payload) => {
        try {
          const updatedCart = await cartApi.addItem(
            payload.data.productId,
            payload.data.quantity
          );
          setCart(updatedCart);

          // –£–≤–µ–¥–æ–º–ª—è–µ–º –æ–± –æ–±–Ω–æ–≤–ª–µ–Ω–∏–∏ –∫–æ—Ä–∑–∏–Ω—ã
          eventBus.emit<CartUpdatedPayload>({
            type: MicroFrontendEvent.CartUpdated,
            data: { cart: updatedCart },
            timestamp: Date.now(),
            source: 'cart'
          });
        } catch (err) {
          console.error('Failed to add item to cart:', err);
        }
      }
    );
  };

  const handleUpdateQuantity = async (
    productId: string,
    quantity: number
  ) => {
    try {
      const updatedCart = await cartApi.updateQuantity(productId, quantity);
      setCart(updatedCart);
    } catch (err) {
      console.error('Failed to update quantity:', err);
    }
  };

  const handleRemoveItem = async (productId: string) => {
    try {
      const updatedCart = await cartApi.removeItem(productId);
      setCart(updatedCart);
    } catch (err) {
      console.error('Failed to remove item:', err);
    }
  };

  const handleClearCart = async () => {
    try {
      await cartApi.clear();
      setCart(null);
    } catch (err) {
      console.error('Failed to clear cart:', err);
    }
  };

  if (loading) return <div>Loading cart...</div>;
  if (!cart || cart.items.length === 0) {
    return <div className="empty-cart">Your cart is empty</div>;
  }

  return (
    <div className="cart">
      <h2>Shopping Cart</h2>
      <div className="cart-items">
        {cart.items.map(item => (
          <CartItem
            key={item.productId}
            item={item}
            onUpdateQuantity={handleUpdateQuantity}
            onRemove={handleRemoveItem}
          />
        ))}
      </div>
      <div className="cart-summary">
        <div className="total-items">Total items: {cart.totalItems}</div>
        <div className="total-price">
          Total: {cart.totalPrice} {cart.currency}
        </div>
        <button className="checkout-button">Proceed to Checkout</button>
        <button className="clear-button" onClick={handleClearCart}>
          Clear Cart
        </button>
      </div>
    </div>
  );
};

