### 4. Event Bus –¥–ª—è –∫–æ–º–º—É–Ω–∏–∫–∞—Ü–∏–∏ –º–µ–∂–¥—É –º–∏–∫—Ä–æ—Ñ—Ä–æ–Ω—Ç–µ–Ω–¥–∞–º–∏

#### 4.1 –†–µ–∞–ª–∏–∑–∞—Ü–∏—è Event Bus

```typescript
// shared-types/src/eventBus.ts
import {
  MicroFrontendEvent,
  EventPayload
} from './events';

type EventHandler<T = any> = (payload: EventPayload<T>) => void;

export class EventBus {
  private handlers: Map<MicroFrontendEvent, Set<EventHandler>> = new Map();
  private history: EventPayload[] = [];
  private maxHistorySize = 100;

  /**
   * –ü–æ–¥–ø–∏—Å–∫–∞ –Ω–∞ —Å–æ–±—ã—Ç–∏–µ
   */
  on<T = any>(
    event: MicroFrontendEvent,
    handler: EventHandler<T>
  ): () => void {
    if (!this.handlers.has(event)) {
      this.handlers.set(event, new Set());
    }

    this.handlers.get(event)!.add(handler);

    // –í–æ–∑–≤—Ä–∞—â–∞–µ–º —Ñ—É–Ω–∫—Ü–∏—é –¥–ª—è –æ—Ç–ø–∏—Å–∫–∏
    return () => {
      this.off(event, handler);
    };
  }

  /**
   * –ü–æ–¥–ø–∏—Å–∫–∞ –Ω–∞ —Å–æ–±—ã—Ç–∏–µ (–≤—ã–ø–æ–ª–Ω–∏—Ç—Å—è –æ–¥–∏–Ω —Ä–∞–∑)
   */
  once<T = any>(
    event: MicroFrontendEvent,
    handler: EventHandler<T>
  ): void {
    const wrappedHandler: EventHandler<T> = (payload) => {
      handler(payload);
      this.off(event, wrappedHandler);
    };

    this.on(event, wrappedHandler);
  }

  /**
   * –û—Ç–ø–∏—Å–∫–∞ –æ—Ç —Å–æ–±—ã—Ç–∏—è
   */
  off<T = any>(
    event: MicroFrontendEvent,
    handler: EventHandler<T>
  ): void {
    const handlers = this.handlers.get(event);
    if (handlers) {
      handlers.delete(handler);
      if (handlers.size === 0) {
        this.handlers.delete(event);
      }
    }
  }

  /**
   * –û—Ç–ø—Ä–∞–≤–∫–∞ —Å–æ–±—ã—Ç–∏—è
   */
  emit<T = any>(payload: EventPayload<T>): void {
    // –î–æ–±–∞–≤–ª—è–µ–º –≤ –∏—Å—Ç–æ—Ä–∏—é
    this.history.push(payload);
    if (this.history.length > this.maxHistorySize) {
      this.history.shift();
    }

    // –í—ã–∑—ã–≤–∞–µ–º –æ–±—Ä–∞–±–æ—Ç—á–∏–∫–∏
    const handlers = this.handlers.get(payload.type);
    if (handlers) {
      handlers.forEach(handler => {
        try {
          handler(payload);
        } catch (error) {
          console.error(`Error in event handler for ${payload.type}:`, error);
        }
      });
    }

    // –õ–æ–≥–∏—Ä–æ–≤–∞–Ω–∏–µ –¥–ª—è –æ—Ç–ª–∞–¥–∫–∏
    if (process.env.NODE_ENV === 'development') {
      console.log('[EventBus]', payload.type, payload);
    }
  }

  /**
   * –ü–æ–ª—É—á–µ–Ω–∏–µ –∏—Å—Ç–æ—Ä–∏–∏ —Å–æ–±—ã—Ç–∏–π
   */
  getHistory(
    filter?: MicroFrontendEvent | ((payload: EventPayload) => boolean)
  ): EventPayload[] {
    if (!filter) return [...this.history];

    if (typeof filter === 'function') {
      return this.history.filter(filter);
    }

    return this.history.filter(payload => payload.type === filter);
  }

  /**
   * –û—á–∏—Å—Ç–∫–∞ –∏—Å—Ç–æ—Ä–∏–∏
   */
  clearHistory(): void {
    this.history = [];
  }

  /**
   * –£–¥–∞–ª–µ–Ω–∏–µ –≤—Å–µ—Ö –æ–±—Ä–∞–±–æ—Ç—á–∏–∫–æ–≤
   */
  clear(): void {
    this.handlers.clear();
  }
}

// –ì–ª–æ–±–∞–ª—å–Ω—ã–π —ç–∫–∑–µ–º–ø–ª—è—Ä Event Bus
export const eventBus = new EventBus();
```

