#### 5.1 –î–∏–Ω–∞–º–∏—á–µ—Å–∫–∞—è –∑–∞–≥—Ä—É–∑–∫–∞ Remote –º–æ–¥—É–ª–µ–π

```typescript
// host/src/utils/loadRemoteModule.ts
interface RemoteConfig {
  url: string;
  scope: string;
  module: string;
}

const remoteConfigs: Record<string, RemoteConfig> = {
  products: {
    url: 'http://localhost:3001/remoteEntry.js',
    scope: 'products',
    module: './ProductList'
  },
  cart: {
    url: 'http://localhost:3002/remoteEntry.js',
    scope: 'cart',
    module: './Cart'
  }
};

export async function loadRemoteModule<T = any>(
  remoteName: string,
  moduleName: string
): Promise<T> {
  const config = remoteConfigs[remoteName];
  if (!config) {
    throw new Error(`Remote '${remoteName}' not found`);
  }

  try {
    // –ó–∞–≥—Ä—É–∂–∞–µ–º remote container
    await loadRemoteContainer(config.url, config.scope);

    // –ü–æ–ª—É—á–∞–µ–º –º–æ–¥—É–ª—å –∏–∑ container
    const container = (window as any)[config.scope];
    await container.init(__webpack_share_scopes__.default);

    const factory = await container.get(moduleName || config.module);
    const module = factory();

    return module;
  } catch (error) {
    console.error(`Failed to load remote module ${remoteName}/${moduleName}:`, error);
    throw error;
  }
}

function loadRemoteContainer(url: string, scope: string): Promise<void> {
  return new Promise((resolve, reject) => {
    // –ü—Ä–æ–≤–µ—Ä—è–µ–º, –Ω–µ –∑–∞–≥—Ä—É–∂–µ–Ω –ª–∏ —É–∂–µ —ç—Ç–æ—Ç remote
    if ((window as any)[scope]) {
      resolve();
      return;
    }

    const script = document.createElement('script');
    script.src = url;
    script.type = 'text/javascript';
    script.async = true;

    script.onload = () => {
      resolve();
    };

    script.onerror = () => {
      reject(new Error(`Failed to load remote script: ${url}`));
    };

    document.head.appendChild(script);
  });
}

// –ò—Å–ø–æ–ª—å–∑–æ–≤–∞–Ω–∏–µ
import React, { useEffect, useState } from 'react';
import { loadRemoteModule } from '../utils/loadRemoteModule';

export const DynamicProductList: React.FC = () => {
  const [ProductList, setProductList] = useState<React.ComponentType | null>(
    null
  );
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    loadRemoteModule('products', './ProductList')
      .then(module => {
        setProductList(() => module.default);
      })
      .catch(err => {
        setError(err.message);
      });
  }, []);

  if (error) return <div>Error: {error}</div>;
  if (!ProductList) return <div>Loading...</div>;

  return <ProductList />;
};
