// host/src/config/remotes.ts
export interface RemoteVersion {
  version: string;
  url: string;
  fallbackUrl?: string;
}

export interface RemoteManifest {
  [key: string]: {
    versions: RemoteVersion[];
    currentVersion: string;
  };
}

export const remoteManifest: RemoteManifest = {
  products: {
    currentVersion: '1.2.0',
    versions: [
      {
        version: '1.2.0',
        url: 'http://localhost:3001/remoteEntry.js'
      },
      {
        version: '1.1.0',
        url: 'http://localhost:3001/v1.1.0/remoteEntry.js',
        fallbackUrl: 'http://cdn.example.com/products/1.1.0/remoteEntry.js'
      }
    ]
  },
  cart: {
    currentVersion: '2.0.0',
    versions: [
      {
        version: '2.0.0',
        url: 'http://localhost:3002/remoteEntry.js'
      }
    ]
  }
};

// host/src/utils/remoteLoader.ts
export class RemoteLoader {
  private loadedRemotes: Map<string, any> = new Map();

  async loadRemote(
    name: string,
    version?: string
  ): Promise<any> {
    const cacheKey = `${name}@${version || 'current'}`;

    // –ü—Ä–æ–≤–µ—Ä—è–µ–º –∫–µ—à
    if (this.loadedRemotes.has(cacheKey)) {
      return this.loadedRemotes.get(cacheKey);
    }

    const manifest = remoteManifest[name];
    if (!manifest) {
      throw new Error(`Remote '${name}' not found in manifest`);
    }

    const targetVersion = version || manifest.currentVersion;
    const versionConfig = manifest.versions.find(
      v => v.version === targetVersion
    );

    if (!versionConfig) {
      throw new Error(
        `Version '${targetVersion}' of remote '${name}' not found`
      );
    }

    try {
      // –ü—ã—Ç–∞–µ–º—Å—è –∑–∞–≥—Ä—É–∑–∏—Ç—å primary URL
      const remote = await this.loadRemoteFromUrl(
        versionConfig.url,
        name
      );
      this.loadedRemotes.set(cacheKey, remote);
      return remote;
    } catch (error) {
      // –ï—Å–ª–∏ –µ—Å—Ç—å fallback URL, –ø—ã—Ç–∞–µ–º—Å—è –µ–≥–æ
      if (versionConfig.fallbackUrl) {
        console.warn(
          `Failed to load ${name} from primary URL, trying fallback...`
        );
        const remote = await this.loadRemoteFromUrl(
          versionConfig.fallbackUrl,
          name
        );
        this.loadedRemotes.set(cacheKey, remote);
        return remote;
      }

      throw error;
    }
  }

  private async loadRemoteFromUrl(
    url: string,
    scope: string
  ): Promise<any> {
    // –†–µ–∞–ª–∏–∑–∞—Ü–∏—è –∑–∞–≥—Ä—É–∑–∫–∏ (–∞–Ω–∞–ª–æ–≥–∏—á–Ω–æ loadRemoteContainer)
    return new Promise((resolve, reject) => {
      if ((window as any)[scope]) {
        resolve((window as any)[scope]);
        return;
      }

      const script = document.createElement('script');
      script.src = url;
      script.onload = () => resolve((window as any)[scope]);
      script.onerror = () => reject(new Error(`Failed to load ${url}`));
      document.head.appendChild(script);
    });
  }
}

export const remoteLoader = new RemoteLoader();
