#### 6.1 Shared State —Å –∏—Å–ø–æ–ª—å–∑–æ–≤–∞–Ω–∏–µ–º Context API

```typescript
// host/src/context/SharedStateContext.tsx
import React, {
  createContext,
  useContext,
  useState,
  useCallback,
  ReactNode
} from 'react';
import { Cart, User } from '@micro-frontends/shared-types';

interface SharedState {
  user: User | null;
  cart: Cart | null;
  isAuthenticated: boolean;
}

interface SharedStateContextValue {
  state: SharedState;
  setUser: (user: User | null) => void;
  setCart: (cart: Cart | null) => void;
  logout: () => void;
}

const SharedStateContext = createContext<
  SharedStateContextValue | undefined
>(undefined);

export const SharedStateProvider: React.FC<{ children: ReactNode }> = ({
  children
}) => {
  const [state, setState] = useState<SharedState>({
    user: null,
    cart: null,
    isAuthenticated: false
  });

  const setUser = useCallback((user: User | null) => {
    setState(prev => ({
      ...prev,
      user,
      isAuthenticated: !!user
    }));
  }, []);

  const setCart = useCallback((cart: Cart | null) => {
    setState(prev => ({
      ...prev,
      cart
    }));
  }, []);

  const logout = useCallback(() => {
    setState({
      user: null,
      cart: null,
      isAuthenticated: false
    });
  }, []);

  return (
    <SharedStateContext.Provider
      value={{
        state,
        setUser,
        setCart,
        logout
      }}
    >
      {children}
    </SharedStateContext.Provider>
  );
};

export function useSharedState(): SharedStateContextValue {
  const context = useContext(SharedStateContext);
  if (!context) {
    throw new Error(
      'useSharedState must be used within SharedStateProvider'
    );
  }
  return context;
}

