```typescript
// src/services/user/user.service.ts
import * as grpc from '@grpc/grpc-js';
import {
  UserServiceService,
  IUserServiceServer
} from '../../generated/user_grpc_pb';
import {
  GetUserRequest,
  CreateUserRequest,
  UpdateUserRequest,
  DeleteUserRequest,
  ListUsersRequest,
  UserResponse,
  DeleteUserResponse
} from '../../generated/user_pb';

class UserServiceImpl implements IUserServiceServer {
  [name: string]: grpc.UntypedHandleCall;

  async getUser(
    call: grpc.ServerUnaryCall<GetUserRequest, UserResponse>,
    callback: grpc.sendUnaryData<UserResponse>
  ): Promise<void> {
    try {
      const userId = call.request.getId();

      // –ë–∏–∑–Ω–µ—Å-–ª–æ–≥–∏–∫–∞ –ø–æ–ª—É—á–µ–Ω–∏—è –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è
      const user = await this.userRepository.findById(userId);

      if (!user) {
        return callback({
          code: grpc.status.NOT_FOUND,
          message: 'User not found'
        });
      }

      const response = new UserResponse();
      response.setId(user.id);
      response.setEmail(user.email);
      response.setName(user.name);
      response.setRolesList(user.roles);
      response.setCreatedAt(user.createdAt.getTime());
      response.setUpdatedAt(user.updatedAt.getTime());

      callback(null, response);
    } catch (error) {
      callback({
        code: grpc.status.INTERNAL,
        message: error.message
      });
    }
  }

  async createUser(
    call: grpc.ServerUnaryCall<CreateUserRequest, UserResponse>,
    callback: grpc.sendUnaryData<UserResponse>
  ): Promise<void> {
    try {
      const { email, name, password, rolesList } = {
        email: call.request.getEmail(),
        name: call.request.getName(),
        password: call.request.getPassword(),
        rolesList: call.request.getRolesList()
      };

      // –í–∞–ª–∏–¥–∞—Ü–∏—è
      if (!email || !name || !password) {
        return callback({
          code: grpc.status.INVALID_ARGUMENT,
          message: 'Missing required fields'
        });
      }

      // –ë–∏–∑–Ω–µ—Å-–ª–æ–≥–∏–∫–∞ —Å–æ–∑–¥–∞–Ω–∏—è –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è
      const user = await this.userRepository.create({
        email,
        name,
        password: await this.hashPassword(password),
        roles: rolesList
      });

      const response = new UserResponse();
      response.setId(user.id);
      response.setEmail(user.email);
      response.setName(user.name);
      response.setRolesList(user.roles);
      response.setCreatedAt(user.createdAt.getTime());
      response.setUpdatedAt(user.updatedAt.getTime());

      callback(null, response);
    } catch (error) {
      callback({
        code: grpc.status.INTERNAL,
        message: error.message
      });
    }
  }

  async listUsers(
    call: grpc.ServerWritableStream<ListUsersRequest, UserResponse>
  ): Promise<void> {
    try {
      const page = call.request.getPage() || 1;
      const limit = call.request.getLimit() || 10;
      const filter = call.request.getFilter();

      const users = await this.userRepository.findAll({
        page,
        limit,
        filter
      });

      for (const user of users) {
        const response = new UserResponse();
        response.setId(user.id);
        response.setEmail(user.email);
        response.setName(user.name);
        response.setRolesList(user.roles);
        response.setCreatedAt(user.createdAt.getTime());
        response.setUpdatedAt(user.updatedAt.getTime());

        call.write(response);
      }

      call.end();
    } catch (error) {
      call.emit('error', {
        code: grpc.status.INTERNAL,
        message: error.message
      });
    }
  }

  private userRepository = new UserRepository();

  private async hashPassword(password: string): Promise<string> {
    // –†–µ–∞–ª–∏–∑–∞—Ü–∏—è —Ö–µ—à–∏—Ä–æ–≤–∞–Ω–∏—è –ø–∞—Ä–æ–ª—è
    return password; // –ó–∞–≥–ª—É—à–∫–∞
  }
}

// –ó–∞–ø—É—Å–∫ gRPC —Å–µ—Ä–≤–µ—Ä–∞
export function startGrpcServer(port: number): grpc.Server {
  const server = new grpc.Server();

  server.addService(
    UserServiceService,
    new UserServiceImpl()
  );

  server.bindAsync(
    `0.0.0.0:${port}`,
    grpc.ServerCredentials.createInsecure(),
    (error, port) => {
      if (error) {
        console.error('Failed to start gRPC server:', error);
        return;
      }
      console.log(`gRPC server running on port ${port}`);
      server.start();
    }
  );

  return server;
}
