#### gRPC Client Implementation

```typescript
// src/clients/user.client.ts
import * as grpc from '@grpc/grpc-js';
import { UserServiceClient } from '../generated/user_grpc_pb';
import {
  GetUserRequest,
  CreateUserRequest,
  UserResponse
} from '../generated/user_pb';
import { promisify } from 'util';

export class UserClient {
  private client: UserServiceClient;

  constructor(serverAddress: string) {
    this.client = new UserServiceClient(
      serverAddress,
      grpc.credentials.createInsecure()
    );
  }

  async getUser(userId: string): Promise<UserResponse> {
    const request = new GetUserRequest();
    request.setId(userId);

    return new Promise((resolve, reject) => {
      this.client.getUser(request, (error, response) => {
        if (error) {
          reject(error);
        } else {
          resolve(response);
        }
      });
    });
  }

  async createUser(data: {
    email: string;
    name: string;
    password: string;
    roles: string[];
  }): Promise<UserResponse> {
    const request = new CreateUserRequest();
    request.setEmail(data.email);
    request.setName(data.name);
    request.setPassword(data.password);
    request.setRolesList(data.roles);

    return new Promise((resolve, reject) => {
      this.client.createUser(request, (error, response) => {
        if (error) {
          reject(error);
        } else {
          resolve(response);
        }
      });
    });
  }

  async listUsers(page: number, limit: number): Promise<UserResponse[]> {
    const request = new ListUsersRequest();
    request.setPage(page);
    request.setLimit(limit);

    return new Promise((resolve, reject) => {
      const users: UserResponse[] = [];
      const stream = this.client.listUsers(request);

      stream.on('data', (user: UserResponse) => {
        users.push(user);
      });

      stream.on('end', () => {
        resolve(users);
      });

      stream.on('error', (error) => {
        reject(error);
      });
    });
  }

  close(): void {
    this.client.close();
  }
}

// –ò—Å–ø–æ–ª—å–∑–æ–≤–∞–Ω–∏–µ –∫–ª–∏–µ–Ω—Ç–∞
