// src/messaging/kafka.service.ts
import { Kafka, Producer, Consumer, EachMessagePayload } from 'kafkajs';
import { DomainEvent, EventType } from '@mycompany/shared-types';

export class KafkaService {
  private kafka: Kafka;
  private producer: Producer;
  private consumers: Map<string, Consumer> = new Map();

  constructor(brokers: string[]) {
    this.kafka = new Kafka({
      clientId: 'my-app',
      brokers
    });

    this.producer = this.kafka.producer();
  }

  async connect(): Promise<void> {
    await this.producer.connect();
    console.log('Kafka producer connected');
  }

  async publish(topic: string, event: DomainEvent): Promise<void> {
    await this.producer.send({
      topic,
      messages: [
        {
          key: event.id,
          value: JSON.stringify(event),
          headers: {
            'event-type': event.type,
            'version': event.version
          }
        }
      ]
    });

    console.log(`Published event to ${topic}:`, event.type);
  }

  async subscribe(
    topic: string,
    groupId: string,
    handler: (event: DomainEvent) => Promise<void>
  ): Promise<void> {
    const consumer = this.kafka.consumer({ groupId });
    await consumer.connect();
    await consumer.subscribe({ topic, fromBeginning: false });

    await consumer.run({
      eachMessage: async ({ topic, partition, message }: EachMessagePayload) => {
        try {
          if (!message.value) return;

          const event: DomainEvent = JSON.parse(message.value.toString());
          await handler(event);
        } catch (error) {
          console.error('Error processing Kafka message:', error);
          // –ó–¥–µ—Å—å –º–æ–∂–Ω–æ –¥–æ–±–∞–≤–∏—Ç—å –ª–æ–≥–∏–∫—É retry –∏–ª–∏ –æ—Ç–ø—Ä–∞–≤–∫—É –≤ DLQ
        }
      }
    });

    this.consumers.set(groupId, consumer);
    console.log(`Subscribed to topic ${topic} with group ${groupId}`);
  }

  async disconnect(): Promise<void> {
    await this.producer.disconnect();

    for (const [groupId, consumer] of this.consumers) {
      await consumer.disconnect();
    }

    this.consumers.clear();
  }
}

