```typescript
// src/discovery/service-registry.ts
export interface ServiceInstance {
  id: string;
  name: string;
  host: string;
  port: number;
  metadata: Record<string, string>;
  healthCheckUrl: string;
}

export class ServiceRegistry {
  private services: Map<string, ServiceInstance[]> = new Map();

  register(service: ServiceInstance): void {
    const instances = this.services.get(service.name) || [];
    instances.push(service);
    this.services.set(service.name, instances);
  }

  deregister(serviceName: string, instanceId: string): void {
    const instances = this.services.get(serviceName) || [];
    const filtered = instances.filter(s => s.id !== instanceId);
    this.services.set(serviceName, filtered);
  }

  getInstances(serviceName: string): ServiceInstance[] {
    return this.services.get(serviceName) || [];
  }

  getHealthyInstance(serviceName: string): ServiceInstance | null {
    const instances = this.getInstances(serviceName);
    // –ü—Ä–æ—Å—Ç–∞—è round-robin —Å—Ç—Ä–∞—Ç–µ–≥–∏—è
    return instances[Math.floor(Math.random() * instances.length)] || null;
  }
}

// Load Balancer
export class LoadBalancer {
  private currentIndex = 0;

  constructor(private registry: ServiceRegistry) {}

  async call<T>(
    serviceName: string,
    path: string
  ): Promise<ServiceResponse<T>> {
    const instance = this.getNextInstance(serviceName);

    if (!instance) {
      return {
        success: false,
        error: {
          code: 'SERVICE_UNAVAILABLE',
          message: `No healthy instances of ${serviceName} found`
        }
      };
    }

    const client = new TypedHttpClient(`http://${instance.host}:${instance.port}`);
    return client.get<T>(path);
  }

  private getNextInstance(serviceName: string): ServiceInstance | null {
    const instances = this.registry.getInstances(serviceName);

    if (instances.length === 0) {
      return null;
    }

    const instance = instances[this.currentIndex % instances.length];
    this.currentIndex++;

    return instance;
  }
}
