// directives/clickOutside.ts
import type { Directive } from 'vue'

interface ClickOutsideElement extends HTMLElement {
  __clickOutsideHandler__?: (event: MouseEvent) => void
}

export const vClickOutside: Directive = {
  mounted(el: ClickOutsideElement, binding) {
    const handler = (event: MouseEvent) => {
      // –ü—Ä–æ–≤–µ—Ä—è–µ–º —á—Ç–æ –∫–ª–∏–∫ –±—ã–ª –≤–Ω–µ —ç–ª–µ–º–µ–Ω—Ç–∞
      if (!el.contains(event.target as Node)) {
        // –í—ã–∑—ã–≤–∞–µ–º callback
        binding.value(event)
      }
    }

    // –°–æ—Ö—Ä–∞–Ω—è–µ–º handler –¥–ª—è cleanup
    el.__clickOutsideHandler__ = handler

    // –î–æ–±–∞–≤–ª—è–µ–º listener —Å –Ω–µ–±–æ–ª—å—à–æ–π –∑–∞–¥–µ—Ä–∂–∫–æ–π
    setTimeout(() => {
      document.addEventListener('click', handler)
    }, 0)
  },

  unmounted(el: ClickOutsideElement) {
    // –û—á–∏—Å—Ç–∫–∞
    if (el.__clickOutsideHandler__) {
      document.removeEventListener('click', el.__clickOutsideHandler__)
      delete el.__clickOutsideHandler__
    }
  }
}
