```typescript
// directives/lazyLoad.ts
import type { Directive } from 'vue'

export const vLazyLoad: Directive = {
  mounted(el: HTMLImageElement, binding) {
    const loadImage = () => {
      el.src = binding.value
      el.classList.add('loaded')
    }

    // IntersectionObserver –¥–ª—è lazy loading
    const observer = new IntersectionObserver((entries) => {
      entries.forEach(entry => {
        if (entry.isIntersecting) {
          loadImage()
          observer.unobserve(el)
        }
      })
    }, {
      rootMargin: '50px' // –ù–∞—á–∞—Ç—å –∑–∞–≥—Ä—É–∑–∫—É –∑–∞ 50px –¥–æ –ø–æ—è–≤–ª–µ–Ω–∏—è
    })

    observer.observe(el)

    // –°–æ—Ö—Ä–∞–Ω—è–µ–º observer –¥–ª—è cleanup
    (el as any).__lazyLoadObserver__ = observer
  },

  unmounted(el: HTMLElement) {
    const observer = (el as any).__lazyLoadObserver__
    if (observer) {
      observer.disconnect()
      delete (el as any).__lazyLoadObserver__
    }
  }
}
```

