```typescript
// composables/useAppContext.ts
import { inject, provide, readonly, ref, type InjectionKey, type Ref } from 'vue'

// ‚úÖ –¢–∏–ø–∏–∑–∏—Ä–æ–≤–∞–Ω–Ω—ã–µ injection keys
export interface AppContext {
  theme: Ref<'light' | 'dark'>
  locale: Ref<string>
  isAuthenticated: Ref<boolean>
}

export const AppContextKey: InjectionKey<AppContext> = Symbol('app-context')

export function provideAppContext() {
  const theme = ref<'light' | 'dark'>('light')
  const locale = ref('en')
  const isAuthenticated = ref(false)

  const context: AppContext = {
    theme: readonly(theme) as Ref<'light' | 'dark'>,
    locale: readonly(locale) as Ref<string>,
    isAuthenticated: readonly(isAuthenticated) as Ref<boolean>
  }

  provide(AppContextKey, context)

  // –ú–µ—Ç–æ–¥—ã –¥–ª—è –∏–∑–º–µ–Ω–µ–Ω–∏—è
  const setTheme = (newTheme: 'light' | 'dark') => {
    theme.value = newTheme
    localStorage.setItem('theme', newTheme)
  }

  const setLocale = (newLocale: string) => {
    locale.value = newLocale
    localStorage.setItem('locale', newLocale)
  }

  return {
    theme,
    locale,
    isAuthenticated,
    setTheme,
    setLocale
  }
}

export function useAppContext() {
  const context = inject(AppContextKey)

  if (!context) {
    throw new Error('useAppContext must be used within provideAppContext')
  }

  return context
}
```

