```typescript
// features/comments/composables/useCommentsContext.ts
import { inject, provide, ref, type InjectionKey, type Ref } from 'vue'

interface Comment {
  id: number
  text: string
  author: string
}

interface CommentsContext {
  comments: Ref<Comment[]>
  addComment: (comment: Omit<Comment, 'id'>) => void
  deleteComment: (id: number) => void
  updateComment: (id: number, text: string) => void
}

const CommentsContextKey: InjectionKey<CommentsContext> = Symbol('comments')

export function provideCommentsContext() {
  const comments = ref<Comment[]>([])

  const addComment = (comment: Omit<Comment, 'id'>) => {
    comments.value.push({
      ...comment,
      id: Date.now()
    })
  }

  const deleteComment = (id: number) => {
    comments.value = comments.value.filter(c => c.id !== id)
  }

  const updateComment = (id: number, text: string) => {
    const comment = comments.value.find(c => c.id === id)
    if (comment) {
      comment.text = text
    }
  }

  const context: CommentsContext = {
    comments,
    addComment,
    deleteComment,
    updateComment
  }

  provide(CommentsContextKey, context)

  return context
}

export function useCommentsContext() {
  const context = inject(CommentsContextKey)

  if (!context) {
    throw new Error('useCommentsContext must be used within CommentsProvider')
  }

  return context
}
```

