**Plugin pattern —Å DI:**

```typescript
// plugins/notifications.ts
import { provide, inject, ref, type InjectionKey, type App } from 'vue'

interface Notification {
  id: number
  message: string
  type: 'success' | 'error' | 'info'
}

interface NotificationsPlugin {
  notifications: Ref<Notification[]>
  notify: (message: string, type: Notification['type']) => void
  remove: (id: number) => void
}

const NotificationsKey: InjectionKey<NotificationsPlugin> = Symbol('notifications')

export const NotificationsPlugin = {
  install(app: App) {
    const notifications = ref<Notification[]>([])

    const notify = (message: string, type: Notification['type'] = 'info') => {
      const id = Date.now()
      notifications.value.push({ id, message, type })

      // –ê–≤—Ç–æ—É–¥–∞–ª–µ–Ω–∏–µ —á–µ—Ä–µ–∑ 3 —Å–µ–∫—É–Ω–¥—ã
      setTimeout(() => {
        remove(id)
      }, 3000)
    }

    const remove = (id: number) => {
      notifications.value = notifications.value.filter(n => n.id !== id)
    }

    const plugin: NotificationsPlugin = {
      notifications,
      notify,
      remove
    }

    // ‚úÖ –ì–ª–æ–±–∞–ª—å–Ω—ã–π provide
    app.provide(NotificationsKey, plugin)
  }
}

export function useNotifications() {
  const notifications = inject(NotificationsKey)

  if (!notifications) {
    throw new Error('NotificationsPlugin not installed')
  }

  return notifications
}
```

