const content = ref('')

const open = (options?: { title?: string; content?: string }) => {
  isOpen.value = true
  title.value = options?.title || ''
  content.value = options?.content || ''
}

const close = () => {
  isOpen.value = false
}

const toggle = () => {
  isOpen.value = !isOpen.value
}

// –í–æ–∑–≤—Ä–∞—â–∞–µ–º Promise –¥–ª—è –∞—Å–∏–Ω—Ö—Ä–æ–Ω–Ω–æ–≥–æ –≤–∑–∞–∏–º–æ–¥–µ–π—Å—Ç–≤–∏—è
