    const handleConfirm = () => {
      close()
      resolve(true)
    }
    const handleCancel = () => {
      close()
      resolve(false)
    }
    // –°–æ—Ö—Ä–∞–Ω—è–µ–º handlers –¥–ª—è –∏—Å–ø–æ–ª—å–∑–æ–≤–∞–Ω–∏—è –≤ template
    ;(window as any).modalConfirm = handleConfirm
    ;(window as any).modalCancel = handleCancel
  })
}

defineExpose({
  open,
  close,
  toggle,
  confirm,
  isOpen: readonly(isOpen)
})
