```typescript
// stores/counter.ts
import { defineStore } from 'pinia'
import { ref, computed } from 'vue'

// ‚úÖ Setup Stores (—Ä–µ–∫–æ–º–µ–Ω–¥—É–µ—Ç—Å—è)
export const useCounterStore = defineStore('counter', () => {
  // State
  const count = ref(0)
  const name = ref('Counter')

  // Getters
  const double = computed(() => count.value * 2)
  const isPositive = computed(() => count.value > 0)

  // Actions
  const increment = () => {
    count.value++
  }

  const decrement = () => {
    count.value--
  }

  const incrementBy = (amount: number) => {
    count.value += amount
  }

  const reset = () => {
    count.value = 0
  }

  return {
    // State
    count,
    name,
    // Getters
    double,
    isPositive,
    // Actions
    increment,
    decrement,
    incrementBy,
    reset
  }
})

// ‚úÖ Options Stores
export const useCounterStore = defineStore('counter', {
  state: () => ({
    count: 0,
    name: 'Counter'
  }),

  getters: {
    double: (state) => state.count * 2,
    isPositive: (state) => state.count > 0
  },

  actions: {
    increment() {
      this.count++
    },
    decrement() {
      this.count--
    },
    incrementBy(amount: number) {
      this.count += amount
    },
    reset() {
      this.count = 0
    }
  }
})
